import { defineConfig } from "vite";
import react from "@vitejs/plugin-react";
import path from "path";
import runtimeErrorOverlay from "@replit/vite-plugin-runtime-error-modal";

export default defineConfig({
  plugins: [
    react(),
    runtimeErrorOverlay(),
    ...(process.env.NODE_ENV !== "production" &&
    process.env.REPL_ID !== undefined
      ? [
          await import("@replit/vite-plugin-cartographer").then((m) =>
            m.cartographer()
          ),
        ]
      : []),
  ],
  resolve: {
    alias: {
      "@": path.resolve(import.meta.dirname, "client", "src"),
      "@shared": path.resolve(import.meta.dirname, "shared"),
      "@assets": path.resolve(import.meta.dirname, "attached_assets"),
    },
  },
  root: path.resolve(import.meta.dirname, "client"),
  build: {
    outDir: path.resolve(import.meta.dirname, "dist/public"),
    emptyOutDir: true,
  },
  server: {
    fs: {
      strict: true,
      deny: ["**/.*"],
    },
    allowedHosts: true,
    host: "0.0.0.0",
    port: 5000,
    proxy: {
      "/api": {
        target: "http://localhost:8000",
        changeOrigin: true,
      },
    },
  },
});

server/db.ts.content:
import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

// Environment detection
const isReplit = !!(process.env.REPL_ID || process.env.REPLIT_DEV_DOMAIN);
const isDevelopment = process.env.NODE_ENV === 'development';
const isProduction = process.env.NODE_ENV === 'production';

// Neon configuration
neonConfig.webSocketConstructor = ws;
neonConfig.pipelineConnect = false;

// SSL Configuration based on environment
if (isDevelopment) {
  // For development (both local and Replit), disable strict SSL verification
  process.env["NODE_TLS_REJECT_UNAUTHORIZED"] = "0";
  console.log("🔓 SSL verification disabled for development");
}

if (!process.env.DATABASE_URL) {
  const environmentHelp = isReplit 
    ? "Make sure PostgreSQL is enabled in your Replit environment" 
    : "Set DATABASE_URL in your local environment (.env file)";

  throw new Error(
    `DATABASE_URL must be set. ${environmentHelp}`
  );
}

// Database connection configuration
const dbConfig: any = {
  connectionString: process.env.DATABASE_URL,
};

// SSL configuration based on environment
if (isProduction) {
  // Production: Enable SSL but don't reject unauthorized (for compatibility)
  dbConfig.ssl = { rejectUnauthorized: false };
} else if (isReplit) {
  // Replit development: No SSL for internal connections
  dbConfig.ssl = false;
} else {
  // Local development: Typically no SSL needed
  dbConfig.ssl = false;
}

// Log connection info (without exposing sensitive data)
const dbUrl = new URL(process.env.DATABASE_URL);
console.log(`🗄️  Database: ${dbUrl.hostname}:${dbUrl.port}${dbUrl.pathname}`);
console.log(`🔧 Environment: ${isReplit ? 'Replit' : 'Local'} (${process.env.NODE_ENV || 'development'})`);

export const pool = new Pool(dbConfig);
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import "dotenv/config";
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "…";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // Environment detection
  const isReplit = process.env.REPL_ID || process.env.REPLIT_DEV_DOMAIN;
  const isProduction = process.env.NODE_ENV === "production";

  // Port configuration - flexible for local and Replit
  const port = parseInt(
    process.env.PORT || (isReplit ? "5000" : "3000"), // Default to 5000 for Replit, 3000 for local
    10
  );

  // Host configuration - 0.0.0.0 for Replit, localhost for local
  const host = isReplit ? "0.0.0.0" : "localhost";

  // Server options
  const serverOptions: any = {
    port,
    host,
  };

  // Only add reusePort for Replit to avoid local conflicts
  if (isReplit) {
    serverOptions.reusePort = true;
  }

  server.listen(serverOptions, () => {
    const environment = isReplit ? "Replit" : "Local";
    log(`🚀 ${environment} server running at http://${host}:${port}`);
    log(`📊 Environment: ${process.env.NODE_ENV || "development"}`);
    log(
      `🔗 Database: ${
        process.env.DATABASE_URL ? "Connected" : "Not configured"
      }`
    );
  });
})();

server/oauth.ts.content:
import passport from 'passport';
import { Strategy as GoogleStrategy } from 'passport-google-oauth20';
import { Strategy as GitHubStrategy } from 'passport-github2';
import { storage } from './storage';
import jwt from 'jsonwebtoken';

const JWT_SECRET = process.env.JWT_SECRET || "your-jwt-secret-key";

export function setupOAuth() {
  // Only setup OAuth if environment variables are provided
  const googleClientId = process.env.GOOGLE_CLIENT_ID;
  const googleClientSecret = process.env.GOOGLE_CLIENT_SECRET;
  const githubClientId = process.env.GITHUB_CLIENT_ID;
  const githubClientSecret = process.env.GITHUB_CLIENT_SECRET;

  // Google OAuth Strategy
  if (googleClientId && googleClientSecret) {
    passport.use(new GoogleStrategy({
      clientID: googleClientId,
      clientSecret: googleClientSecret,
      callbackURL: "/api/auth/google/callback"
    },
    async (accessToken: string, refreshToken: string, profile: any, done: any) => {
    try {
      let user = await storage.getUserByGoogleId(profile.id);

      if (user) {
        return done(null, user);
      }

      // Check if user exists with same email
      if (profile.emails && profile.emails[0]) {
        user = await storage.getUserByEmail(profile.emails[0].value);
        if (user) {
          // Update existing user with Google ID
          user = await storage.updateUser(user.id, {
            googleId: profile.id,
            authProvider: 'google',
            profileImageUrl: user.profileImageUrl || profile.photos?.[0]?.value,
          });
          return done(null, user);
        }
      }

      // Create new user
      const newUser = await storage.createUser({
        username: profile.displayName || `user_${profile.id}`,
        email: profile.emails?.[0]?.value || '',
        firstName: profile.name?.givenName,
        lastName: profile.name?.familyName,
        profileImageUrl: profile.photos?.[0]?.value,
        googleId: profile.id,
        authProvider: 'google',
      });

      return done(null, newUser);
    } catch (error) {
      return done(error, undefined);
    }
    }));
  }

  // GitHub OAuth Strategy
  if (githubClientId && githubClientSecret) {
    passport.use(new GitHubStrategy({
      clientID: githubClientId,
      clientSecret: githubClientSecret,
      callbackURL: "/api/auth/github/callback"
    },
    async (accessToken: string, refreshToken: string, profile: any, done: any) => {
    try {
      let user = await storage.getUserByGithubId(profile.id);

      if (user) {
        return done(null, user);
      }

      // Check if user exists with same email
      if (profile.emails && profile.emails[0]) {
        user = await storage.getUserByEmail(profile.emails[0].value);
        if (user) {
          // Update existing user with GitHub ID
          user = await storage.updateUser(user.id, {
            githubId: profile.id,
            authProvider: 'github',
            profileImageUrl: user.profileImageUrl || profile.photos?.[0]?.value,
          });
          return done(null, user);
        }
      }

      // Create new user
      const newUser = await storage.createUser({
        username: profile.username || `user_${profile.id}`,
        email: profile.emails?.[0]?.value || '',
        firstName: profile.displayName?.split(' ')[0],
        lastName: profile.displayName?.split(' ').slice(1).join(' '),
        profileImageUrl: profile.photos?.[0]?.value,
        githubId: profile.id,
        authProvider: 'github',
      });

      return done(null, newUser);
    } catch (error) {
      return done(error, undefined);
    }
    }));
  }

  passport.serializeUser((user: any, done) => {
    done(null, user.id);
  });

  passport.deserializeUser(async (id: string, done) => {
    try {
      const user = await storage.getUserById(id);
      done(null, user);
    } catch (error) {
      done(error, null);
    }
  });
}

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import {
  insertUserSchema,
  insertSubmissionSchema,
  insertCommunityPostSchema,
  insertPostCommentSchema,
} from "@shared/schema";
import bcrypt from "bcrypt";
import jwt from "jsonwebtoken";
import passport from "passport";
import session from "express-session";
import { setupOAuth } from "./oauth";

const JWT_SECRET = process.env.JWT_SECRET || "your-jwt-secret-key";

// Middleware to verify JWT token
const authenticateToken = (req: any, res: any, next: any) => {
  const authHeader = req.headers["authorization"];
  const token = authHeader && authHeader.split(" ")[1];

  if (!token) {
    return res.status(401).json({ message: "Access token required" });
  }

  jwt.verify(token, JWT_SECRET, (err: any, user: any) => {
    if (err) {
      return res.status(403).json({ message: "Invalid or expired token" });
    }
    req.user = user;
    next();
  });
};

export async function registerRoutes(app: Express): Promise<Server> {
  // Configure session middleware
  app.use(
    session({
      secret: process.env.SESSION_SECRET || "fallback-secret",
      resave: false,
      saveUninitialized: false,
      cookie: { secure: false }, // Set to true in production with HTTPS
    })
  );

  // Initialize passport and session
  app.use(passport.initialize());
  app.use(passport.session());
  setupOAuth();

  // Auth routes
  app.post("/api/auth/register", async (req, res) => {
    try {
      const userData = insertUserSchema.parse(req.body);

      // Check if user already exists
      const existingUser = await storage.getUserByEmail(userData.email);
      if (existingUser) {
        return res.status(400).json({ message: "User already exists" });
      }

      const existingUsername = await storage.getUserByUsername(
        userData.username
      );
      if (existingUsername) {
        return res.status(400).json({ message: "Username already taken" });
      }

      // Hash password
      const saltRounds = 10;
      const passwordHash = userData.passwordHash
        ? await bcrypt.hash(userData.passwordHash, saltRounds)
        : undefined;

      // Create user
      const user = await storage.createUser({
        ...userData,
        passwordHash,
      });

      // Generate JWT token
      const token = jwt.sign(
        { userId: user.id, username: user.username },
        JWT_SECRET,
        {
          expiresIn: "24h",
        }
      );

      res.status(201).json({
        message: "User created successfully",
        token,
        user: {
          id: user.id,
          username: user.username,
          email: user.email,
          firstName: user.firstName,
          lastName: user.lastName,
          profileImageUrl: user.profileImageUrl,
          problemsSolved: user.problemsSolved,
        },
      });
    } catch (error) {
      console.error("Registration error:", error);
      res.status(500).json({ message: "Failed to create user" });
    }
  });

  app.post("/api/auth/login", async (req, res) => {
    try {
      const { email, password } = req.body;

      // Find user by email
      const user = await storage.getUserByEmail(email);
      if (!user) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      // Verify password
      if (!user.passwordHash) {
        return res.status(401).json({ message: "Invalid credentials" });
      }
      const isValidPassword = await bcrypt.compare(password, user.passwordHash);
      if (!isValidPassword) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      // Generate JWT token
      const token = jwt.sign(
        { userId: user.id, username: user.username },
        JWT_SECRET,
        {
          expiresIn: "24h",
        }
      );

      res.json({
        token,
        user: {
          id: user.id,
          username: user.username,
          email: user.email,
          firstName: user.firstName,
          lastName: user.lastName,
          profileImageUrl: user.profileImageUrl,
          problemsSolved: user.problemsSolved,
        },
      });
    } catch (error) {
      console.error("Login error:", error);
      res.status(500).json({ message: "Login failed" });
    }
  });

  // Get current user
  app.get("/api/auth/user", authenticateToken, async (req: any, res) => {
    try {
      const user = await storage.getUserById(req.user.userId);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      res.json({
        id: user.id,
        username: user.username,
        email: user.email,
        firstName: user.firstName,
        lastName: user.lastName,
        profileImageUrl: user.profileImageUrl,
        problemsSolved: user.problemsSolved,
      });
    } catch (error) {
      console.error("Get user error:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // Problem routes
  app.get("/api/problems", async (req, res) => {
    try {
      const { difficulty } = req.query;
      const authHeader = req.headers["authorization"];
      const token = authHeader && authHeader.split(" ")[1];
      let problems;

      // If user is authenticated, include solve status
      if (token) {
        try {
          const decoded = jwt.verify(token, JWT_SECRET) as any;
          const userId = decoded.userId;

          if (difficulty && typeof difficulty === "string") {
            problems = await storage.getProblemsByDifficultyForUser(
              difficulty,
              userId
            );
          } else {
            problems = await storage.getAllProblemsForUser(userId);
          }
        } catch (jwtError) {
          // If token is invalid, fall back to unauthenticated response
          if (difficulty && typeof difficulty === "string") {
            problems = await storage.getProblemsByDifficulty(difficulty);
          } else {
            problems = await storage.getAllProblems();
          }
        }
      } else {
        // Unauthenticated user
        if (difficulty && typeof difficulty === "string") {
          problems = await storage.getProblemsByDifficulty(difficulty);
        } else {
          problems = await storage.getAllProblems();
        }
      }

      res.json(problems);
    } catch (error) {
      console.error("Get problems error:", error);
      res.status(500).json({ message: "Failed to fetch problems" });
    }
  });

  app.get("/api/problems/:id", async (req, res) => {
    try {
      const problem = await storage.getProblemById(req.params.id);
      if (!problem) {
        return res.status(404).json({ message: "Problem not found" });
      }
      res.json(problem);
    } catch (error) {
      console.error("Get problem error:", error);
      res.status(500).json({ message: "Failed to fetch problem" });
    }
  });

  // OAuth routes
  app.get(
    "/api/auth/google",
    passport.authenticate("google", { scope: ["profile", "email"] })
  );

  app.get(
    "/api/auth/google/callback",
    passport.authenticate("google", { failureRedirect: "/" }),
    (req, res) => {
      // Successful authentication, create JWT and redirect
      const user = req.user as any;
      const token = jwt.sign({ userId: user.id }, JWT_SECRET, {
        expiresIn: "7d",
      });
      res.redirect(`/?token=${token}`);
    }
  );

  app.get(
    "/api/auth/github",
    passport.authenticate("github", { scope: ["user:email"] })
  );

  app.get(
    "/api/auth/github/callback",
    passport.authenticate("github", { failureRedirect: "/" }),
    (req, res) => {
      // Successful authentication, create JWT and redirect
      const user = req.user as any;
      const token = jwt.sign({ userId: user.id }, JWT_SECRET, {
        expiresIn: "7d",
      });
      res.redirect(`/?token=${token}`);
    }
  );

  // Submission routes
  app.post("/api/submissions", authenticateToken, async (req: any, res) => {
    try {
      const submissionData = insertSubmissionSchema.parse({
        ...req.body,
        userId: req.user.userId,
      });

      // Simulate SQL query execution
      const isCorrect = await simulateQueryExecution(
        submissionData.query,
        submissionData.problemId
      );
      const executionTime = Math.floor(Math.random() * 500) + 50; // Random execution time

      const submission = await storage.createSubmission({
        ...submissionData,
        isCorrect,
        executionTime,
      });

      // If correct, update user progress
      if (isCorrect) {
        await storage.updateUserProgress(req.user.userId);
      }

      res.json({
        ...submission,
        message: isCorrect
          ? "Query executed successfully!"
          : "Query has errors or incorrect result",
      });
    } catch (error) {
      console.error("Submission error:", error);
      res.status(500).json({ message: "Failed to submit solution" });
    }
  });

  app.get(
    "/api/submissions/user/:userId",
    authenticateToken,
    async (req: any, res) => {
      try {
        // Users can only view their own submissions
        if (req.params.userId !== req.user.userId) {
          return res.status(403).json({ message: "Access denied" });
        }

        const submissions = await storage.getUserSubmissions(req.params.userId);
        res.json(submissions);
      } catch (error) {
        console.error("Get submissions error:", error);
        res.status(500).json({ message: "Failed to fetch submissions" });
      }
    }
  );

  // Leaderboard routes
  app.get("/api/leaderboard", async (req, res) => {
    try {
      const { limit } = req.query;
      const leaderboard = await storage.getLeaderboard(
        limit ? parseInt(limit as string) : undefined
      );

      res.json(
        leaderboard.map((user) => ({
          id: user.id,
          username: user.username,
          firstName: user.firstName,
          lastName: user.lastName,
          profileImageUrl: user.profileImageUrl,
          problemsSolved: user.problemsSolved,
        }))
      );
    } catch (error) {
      console.error("Get leaderboard error:", error);
      res.status(500).json({ message: "Failed to fetch leaderboard" });
    }
  });

  // Community routes
  app.get("/api/community/posts", async (req, res) => {
    try {
      const posts = await storage.getAllCommunityPosts();
      res.json(
        posts.map((post) => ({
          ...post,
          user: {
            id: post.user.id,
            username: post.user.username,
            firstName: post.user.firstName,
            lastName: post.user.lastName,
            profileImageUrl: post.user.profileImageUrl,
          },
        }))
      );
    } catch (error) {
      console.error("Get community posts error:", error);
      res.status(500).json({ message: "Failed to fetch community posts" });
    }
  });

  app.post("/api/community/posts", authenticateToken, async (req: any, res) => {
    try {
      const postData = insertCommunityPostSchema.parse({
        ...req.body,
        userId: req.user.userId,
      });

      const post = await storage.createCommunityPost(postData);
      res.status(201).json(post);
    } catch (error) {
      console.error("Create post error:", error);
      res.status(500).json({ message: "Failed to create post" });
    }
  });

  app.post(
    "/api/community/posts/:postId/like",
    authenticateToken,
    async (req: any, res) => {
      try {
        await storage.likeCommunityPost(req.user.userId, req.params.postId);
        res.json({ message: "Post liked successfully" });
      } catch (error) {
        console.error("Like post error:", error);
        res.status(500).json({ message: "Failed to like post" });
      }
    }
  );

  app.delete(
    "/api/community/posts/:postId/like",
    authenticateToken,
    async (req: any, res) => {
      try {
        await storage.unlikeCommunityPost(req.user.userId, req.params.postId);
        res.json({ message: "Post unliked successfully" });
      } catch (error) {
        console.error("Unlike post error:", error);
        res.status(500).json({ message: "Failed to unlike post" });
      }
    }
  );

  app.get("/api/community/posts/:postId/comments", async (req, res) => {
    try {
      const comments = await storage.getPostComments(req.params.postId);
      res.json(
        comments.map((comment) => ({
          ...comment,
          user: {
            id: comment.user.id,
            username: comment.user.username,
            firstName: comment.user.firstName,
            lastName: comment.user.lastName,
            profileImageUrl: comment.user.profileImageUrl,
          },
        }))
      );
    } catch (error) {
      console.error("Get comments error:", error);
      res.status(500).json({ message: "Failed to fetch comments" });
    }
  });

  app.post(
    "/api/community/posts/:postId/comments",
    authenticateToken,
    async (req: any, res) => {
      try {
        const commentData = insertPostCommentSchema.parse({
          ...req.body,
          userId: req.user.userId,
          postId: req.params.postId,
        });

        const comment = await storage.createPostComment(commentData);
        res.status(201).json(comment);
      } catch (error) {
        console.error("Create comment error:", error);
        res.status(500).json({ message: "Failed to create comment" });
      }
    }
  );

  const httpServer = createServer(app);
  return httpServer;
}

// Helper functions
async function simulateQueryExecution(
  query: string,
  problemId: string
): Promise<boolean> {
  // This is a simplified simulation. In a real app, you'd run the query against a sandbox database.
  const problem = await storage.getProblemById(problemId);
  if (!problem) return false;

  // Simple validation: check if query contains basic SQL keywords and structure
  const normalizedQuery = query.toLowerCase().trim();

  // Basic checks for different problem types
  if (problem.title.toLowerCase().includes("sum")) {
    return (
      normalizedQuery.includes("select") &&
      normalizedQuery.includes("from") &&
      (normalizedQuery.includes("sum") || normalizedQuery.includes("+"))
    );
  }

  if (problem.title.toLowerCase().includes("join")) {
    return (
      normalizedQuery.includes("select") &&
      normalizedQuery.includes("from") &&
      normalizedQuery.includes("join")
    );
  }

  // Default validation: must contain SELECT and FROM
  return normalizedQuery.includes("select") && normalizedQuery.includes("from");
}

server/storage.ts.content:
import {
  users,
  problems,
  submissions,
  communityPosts,
  postLikes,
  postComments,
  type User,
  type InsertUser,
  type Problem,
  type InsertProblem,
  type Submission,
  type InsertSubmission,
  type CommunityPost,
  type InsertCommunityPost,
  type PostComment,
  type InsertPostComment,
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, sql, and } from "drizzle-orm";

export interface IStorage {
  // User operations
  getUserById(id: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  getUserByGoogleId(googleId: string): Promise<User | undefined>;
  getUserByGithubId(githubId: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  updateUser(id: string, user: Partial<InsertUser>): Promise<User>;
  getLeaderboard(limit?: number): Promise<User[]>;
  updateUserProgress(userId: string): Promise<void>;

  // Problem operations
  getAllProblems(): Promise<(Problem & { solvedCount: number })[]>;
  getAllProblemsForUser(userId: string): Promise<(Problem & { solvedCount: number; isUserSolved: boolean })[]>;
  getProblemById(id: string): Promise<Problem | undefined>;
  getProblemsByDifficulty(difficulty: string): Promise<(Problem & { solvedCount: number })[]>;
  getProblemsByDifficultyForUser(difficulty: string, userId: string): Promise<(Problem & { solvedCount: number; isUserSolved: boolean })[]>;
  createProblem(problem: InsertProblem): Promise<Problem>;

  // Submission operations
  createSubmission(submission: InsertSubmission): Promise<Submission>;
  getUserSubmissions(userId: string): Promise<Submission[]>;
  getUserSubmissionForProblem(userId: string, problemId: string): Promise<Submission[]>;

  // Community operations
  getAllCommunityPosts(): Promise<(CommunityPost & { user: User })[]>;
  createCommunityPost(post: InsertCommunityPost): Promise<CommunityPost>;
  likeCommunityPost(userId: string, postId: string): Promise<void>;
  unlikeCommunityPost(userId: string, postId: string): Promise<void>;
  getPostComments(postId: string): Promise<(PostComment & { user: User })[]>;
  createPostComment(comment: InsertPostComment): Promise<PostComment>;

}

export class DatabaseStorage implements IStorage {
  // User operations
  async getUserById(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.email, email));
    return user;
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.username, username));
    return user;
  }

  async getUserByGoogleId(googleId: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.googleId, googleId));
    return user;
  }

  async getUserByGithubId(githubId: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.githubId, githubId));
    return user;
  }

  async createUser(userData: InsertUser): Promise<User> {
    const [user] = await db.insert(users).values(userData).returning();
    return user;
  }

  async updateUser(id: string, userData: Partial<InsertUser>): Promise<User> {
    const [user] = await db
      .update(users)
      .set({ ...userData, updatedAt: new Date() })
      .where(eq(users.id, id))
      .returning();
    return user;
  }

  async getLeaderboard(limit: number = 50): Promise<User[]> {
    return await db
      .select()
      .from(users)
      .orderBy(desc(users.problemsSolved))
      .limit(limit);
  }

  async updateUserProgress(userId: string): Promise<void> {
    await db
      .update(users)
      .set({
        problemsSolved: sql`${users.problemsSolved} + 1`,
        updatedAt: new Date(),
      })
      .where(eq(users.id, userId));
  }

  // Problem operations
  async getAllProblems(): Promise<(Problem & { solvedCount: number })[]> {
    const result = await db
      .select({
        id: problems.id,
        title: problems.title,
        description: problems.description,
        difficulty: problems.difficulty,
        tags: problems.tags,
        companies: problems.companies,
        schema: problems.schema,
        expectedOutput: problems.expectedOutput,
        hints: problems.hints,
        createdAt: problems.createdAt,
        updatedAt: problems.updatedAt,
        solvedCount: sql<number>`COALESCE(COUNT(DISTINCT CASE WHEN ${submissions.isCorrect} = true THEN ${submissions.userId} END), 0)`,
      })
      .from(problems)
      .leftJoin(submissions, eq(problems.id, submissions.problemId))
      .groupBy(problems.id)
      .orderBy(problems.title);

    return result.map(row => ({
      ...row,
      solvedCount: Number(row.solvedCount)
    }));
  }

  async getProblemById(id: string): Promise<Problem | undefined> {
    const [problem] = await db.select().from(problems).where(eq(problems.id, id));
    return problem;
  }

  async getProblemsByDifficulty(difficulty: string): Promise<(Problem & { solvedCount: number })[]> {
    const result = await db
      .select({
        id: problems.id,
        title: problems.title,
        description: problems.description,
        difficulty: problems.difficulty,
        tags: problems.tags,
        companies: problems.companies,
        schema: problems.schema,
        expectedOutput: problems.expectedOutput,
        hints: problems.hints,
        createdAt: problems.createdAt,
        updatedAt: problems.updatedAt,
        solvedCount: sql<number>`COALESCE(COUNT(DISTINCT CASE WHEN ${submissions.isCorrect} = true THEN ${submissions.userId} END), 0)`,
      })
      .from(problems)
      .leftJoin(submissions, eq(problems.id, submissions.problemId))
      .where(eq(problems.difficulty, difficulty))
      .groupBy(problems.id)
      .orderBy(problems.title);

    return result.map(row => ({
      ...row,
      solvedCount: Number(row.solvedCount)
    }));
  }

  async getAllProblemsForUser(userId: string): Promise<(Problem & { solvedCount: number; isUserSolved: boolean })[]> {
    const result = await db
      .select({
        id: problems.id,
        title: problems.title,
        description: problems.description,
        difficulty: problems.difficulty,
        tags: problems.tags,
        companies: problems.companies,
        schema: problems.schema,
        expectedOutput: problems.expectedOutput,
        hints: problems.hints,
        createdAt: problems.createdAt,
        updatedAt: problems.updatedAt,
        solvedCount: sql<number>`COALESCE(COUNT(DISTINCT CASE WHEN ${submissions.isCorrect} = true THEN ${submissions.userId} END), 0)`,
        isUserSolved: sql<boolean>`COALESCE(MAX(CASE WHEN ${submissions.userId} = ${userId} AND ${submissions.isCorrect} = true THEN 1 ELSE 0 END), 0) = 1`,
      })
      .from(problems)
      .leftJoin(submissions, eq(problems.id, submissions.problemId))
      .groupBy(problems.id)
      .orderBy(problems.title);

    return result.map(row => ({
      ...row,
      solvedCount: Number(row.solvedCount),
      isUserSolved: Boolean(row.isUserSolved)
    }));
  }

  async getProblemsByDifficultyForUser(difficulty: string, userId: string): Promise<(Problem & { solvedCount: number; isUserSolved: boolean })[]> {
    const result = await db
      .select({
        id: problems.id,
        title: problems.title,
        description: problems.description,
        difficulty: problems.difficulty,
        tags: problems.tags,
        companies: problems.companies,
        schema: problems.schema,
        expectedOutput: problems.expectedOutput,
        hints: problems.hints,
        createdAt: problems.createdAt,
        updatedAt: problems.updatedAt,
        solvedCount: sql<number>`COALESCE(COUNT(DISTINCT CASE WHEN ${submissions.isCorrect} = true THEN ${submissions.userId} END), 0)`,
        isUserSolved: sql<boolean>`COALESCE(MAX(CASE WHEN ${submissions.userId} = ${userId} AND ${submissions.isCorrect} = true THEN 1 ELSE 0 END), 0) = 1`,
      })
      .from(problems)
      .leftJoin(submissions, eq(problems.id, submissions.problemId))
      .where(eq(problems.difficulty, difficulty))
      .groupBy(problems.id)
      .orderBy(problems.title);

    return result.map(row => ({
      ...row,
      solvedCount: Number(row.solvedCount),
      isUserSolved: Boolean(row.isUserSolved)
    }));
  }

  async createProblem(problemData: InsertProblem): Promise<Problem> {
    const [problem] = await db.insert(problems).values([problemData]).returning();
    return problem;
  }


  // Submission operations
  async createSubmission(submissionData: InsertSubmission): Promise<Submission> {
    const [submission] = await db.insert(submissions).values(submissionData).returning();
    return submission;
  }

  async getUserSubmissions(userId: string): Promise<Submission[]> {
    return await db
      .select()
      .from(submissions)
      .where(eq(submissions.userId, userId))
      .orderBy(desc(submissions.submittedAt));
  }

  async getUserSubmissionForProblem(userId: string, problemId: string): Promise<Submission[]> {
    return await db
      .select()
      .from(submissions)
      .where(and(eq(submissions.userId, userId), eq(submissions.problemId, problemId)))
      .orderBy(desc(submissions.submittedAt));
  }

  // Community operations
  async getAllCommunityPosts(): Promise<(CommunityPost & { user: User })[]> {
    return await db
      .select({
        id: communityPosts.id,
        userId: communityPosts.userId,
        content: communityPosts.content,
        codeSnippet: communityPosts.codeSnippet,
        likes: communityPosts.likes,
        comments: communityPosts.comments,
        createdAt: communityPosts.createdAt,
        updatedAt: communityPosts.updatedAt,
        user: users,
      })
      .from(communityPosts)
      .innerJoin(users, eq(communityPosts.userId, users.id))
      .orderBy(desc(communityPosts.createdAt));
  }

  async createCommunityPost(postData: InsertCommunityPost): Promise<CommunityPost> {
    const [post] = await db.insert(communityPosts).values(postData).returning();
    return post;
  }

  async likeCommunityPost(userId: string, postId: string): Promise<void> {
    await db.insert(postLikes).values({ userId, postId });
    await db
      .update(communityPosts)
      .set({ likes: sql`${communityPosts.likes} + 1` })
      .where(eq(communityPosts.id, postId));
  }

  async unlikeCommunityPost(userId: string, postId: string): Promise<void> {
    await db
      .delete(postLikes)
      .where(and(eq(postLikes.userId, userId), eq(postLikes.postId, postId)));
    await db
      .update(communityPosts)
      .set({ likes: sql`${communityPosts.likes} - 1` })
      .where(eq(communityPosts.id, postId));
  }

  async getPostComments(postId: string): Promise<(PostComment & { user: User })[]> {
    return await db
      .select({
        id: postComments.id,
        userId: postComments.userId,
        postId: postComments.postId,
        content: postComments.content,
        createdAt: postComments.createdAt,
        user: users,
      })
      .from(postComments)
      .innerJoin(users, eq(postComments.userId, users.id))
      .where(eq(postComments.postId, postId))
      .orderBy(postComments.createdAt);
  }

  async createPostComment(commentData: InsertPostComment): Promise<PostComment> {
    const [comment] = await db.insert(postComments).values(commentData).returning();
    await db
      .update(communityPosts)
      .set({ comments: sql`${communityPosts.comments} + 1` })
      .where(eq(communityPosts.id, commentData.postId));
    return comment;
  }

}

export const storage = new DatabaseStorage();

