client/src/pages/problem-detail.tsx.content:
import { useParams, useLocation } from "wouter";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import {
  ArrowLeft,
  Users,
  Star,
  Lightbulb,
  Play,
  Save,
  TrendingUp,
  ChevronLeft,
  ChevronRight,
  MessageSquare,
  CheckCircle,
  FileText,
  Code2,
  Dumbbell,
  Timer,
  RotateCcw,
  Pause,
  Square,
  ChevronDown,
} from "lucide-react";
import { useState, useMemo, useCallback, useEffect, useRef } from "react";
import { Link } from "wouter";
import ReactMarkdown from "react-markdown";
import remarkGfm from "remark-gfm";
import CodeMirror from "@uiw/react-codemirror";
import { sql, PostgreSQL } from "@codemirror/lang-sql";
import { autocompletion } from "@codemirror/autocomplete";
import { EditorView, keymap, placeholder } from "@codemirror/view";
import { defaultKeymap, indentWithTab } from "@codemirror/commands";
import { oneDark } from "@codemirror/theme-one-dark";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";

import { problemsApi, submissionsApi } from "@/lib/auth";
import { useAuth } from "@/hooks/use-auth";
import { useToast } from "@/hooks/use-toast";
import SQLEditor from "@/components/sql-editor";
import TableDisplay from "@/components/table-display";
import ResizableSplitter from "@/components/resizable-splitter";
import VerticalResizableSplitter from "@/components/vertical-resizable-splitter";
import { CompanyLogo } from "@/components/CompanyLogo";
import { DifficultyBadge } from "@/components/DifficultyBadge";
import "../components/AnimatedFields.css";

// Editor and Output Split Component
function EditorOutputSplit({
  problem,
  handleRunQuery,
  handleSubmitSolution,
  onDifficultyClick,
  onCompanyClick,
}: {
  problem: any;
  handleRunQuery: (query: string) => Promise<any>;
  handleSubmitSolution: (query: string) => Promise<any>;
  onDifficultyClick: (difficulty: string) => void;
  onCompanyClick: (company: string) => void;
}) {
  const [query, setQuery] = useState(problem?.question?.starterQuery || "");
  const [result, setResult] = useState<any>(null);
  const [isRunning, setIsRunning] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [showOutput, setShowOutput] = useState(false);

  const selectedCompany = problem?.company || "NY Times";
  const selectedDifficulty = problem?.difficulty || "Medium";

  // Timer functionality
  const [timerSeconds, setTimerSeconds] = useState(0);
  const [isTimerRunning, setIsTimerRunning] = useState(false);
  const timerRef = useRef<number | null>(null);

  // Database selection state
  const [selectedDatabase, setSelectedDatabase] = useState("PostgreSQL 14");

  // Use ref to avoid recreating extensions on every keystroke
  const handleRunRef = useRef<() => void>(() => {});

  // Update ref on every render
  useEffect(() => {
    handleRunRef.current = handleRun;
  });

  // Initialize query when problem loads
  useEffect(() => {
    if (problem?.question?.starterQuery) {
      setQuery(problem.question.starterQuery);
    } else if (
      problem?.question?.tables &&
      problem.question.tables.length > 0
    ) {
      const firstTable = problem.question.tables[0];
      const tableName = firstTable.name;
      // Use proper quoting for PostgreSQL
      setQuery(`SELECT * FROM "${tableName}";`);
    }
  }, [problem]);

  // Detect dark mode with reactivity
  const [isDarkMode, setIsDarkMode] = useState(() => {
    if (typeof window !== "undefined") {
      return document.documentElement.classList.contains("dark");
    }
    return false;
  });

  useEffect(() => {
    const observer = new MutationObserver(() => {
      setIsDarkMode(document.documentElement.classList.contains("dark"));
    });

    observer.observe(document.documentElement, {
      attributes: true,
      attributeFilter: ["class"],
    });

    return () => observer.disconnect();
  }, []);

  // Timer effect
  useEffect(() => {
    if (isTimerRunning) {
      timerRef.current = window.setInterval(() => {
        setTimerSeconds((prev) => prev + 1);
      }, 1000);
    } else {
      if (timerRef.current) {
        window.clearInterval(timerRef.current);
        timerRef.current = null;
      }
    }

    return () => {
      if (timerRef.current) {
        window.clearInterval(timerRef.current);
      }
    };
  }, [isTimerRunning]);

  // Format timer display (MM:SS)
  const formatTimer = (seconds: number) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes.toString().padStart(2, "0")}:${remainingSeconds
      .toString()
      .padStart(2, "0")}`;
  };

  // Timer controls
  const startTimer = () => {
    setIsTimerRunning(true);
  };

  const pauseTimer = () => {
    setIsTimerRunning(false);
  };

  const resetTimerOnly = () => {
    setIsTimerRunning(false);
    setTimerSeconds(0);
  };

  // Reset query and timer to initial state
  const resetQuery = () => {
    if (problem?.question?.starterQuery) {
      setQuery(problem.question.starterQuery);
    } else if (
      problem?.question?.tables &&
      problem.question.tables.length > 0
    ) {
      const firstTable = problem.question.tables[0];
      const tableName = firstTable.name;
      setQuery(`SELECT * FROM "${tableName}";`);
    } else {
      setQuery("");
    }
    setResult(null);
    setShowOutput(false);
    // Also reset the timer
    setIsTimerRunning(false);
    setTimerSeconds(0);
  };

  const handleRun = useCallback(async () => {
    if (!query.trim()) return;

    setIsRunning(true);
    setShowOutput(true);
    try {
      const runResult = await handleRunQuery(query);
      setResult(runResult);
    } catch (error) {
      setResult({
        error: true,
        message:
          error instanceof Error ? error.message : "Query execution failed",
      });
    } finally {
      setIsRunning(false);
    }
  }, [query, handleRunQuery]);

  const handleSubmit = useCallback(async () => {
    if (!query.trim()) return;

    setIsSubmitting(true);
    setShowOutput(true);
    try {
      const submitResult = await handleSubmitSolution(query);
      setResult(submitResult);
      localStorage.setItem("sqlgym_last_query", query);
    } catch (error) {
      setResult({
        error: true,
        message: error instanceof Error ? error.message : "Submission failed",
      });
    } finally {
      setIsSubmitting(false);
    }
  }, [query, handleSubmitSolution]);

  // Generate dynamic placeholder based on first table in problem
  const generatePlaceholder = () => {
    if (problem?.question?.tables && problem.question.tables.length > 0) {
      const firstTable = problem.question.tables[0];
      const tableName = firstTable.name;
      // Use proper quoting for PostgreSQL
      return `-- Write your SQL query here\nSELECT * FROM "${tableName}";`;
    }
    return "-- Write your SQL query here\nSELECT \n    column1,\n    column2\nFROM table_name\nWHERE condition;";
  };

  // Configure CodeMirror extensions and theme
  const extensions = useMemo(
    () => [
      sql({
        dialect: PostgreSQL,
        upperCaseKeywords: true,
        schema: {
          customers: ["id", "name", "email"],
          employees: ["id", "name", "department"],
          orders: ["id", "customer_id", "total"],
          order_items: ["id", "order_id", "price", "quantity"],
        },
      }),
      autocompletion(),
      EditorView.lineWrapping,
      placeholder(generatePlaceholder()),
      keymap.of([
        ...defaultKeymap,
        indentWithTab,
        {
          key: "Mod-Enter",
          run: () => {
            handleRunRef.current();
            return true;
          },
        },
      ]),
    ],
    [problem, isDarkMode]
  );

  const theme = useMemo(() => {
    if (isDarkMode) {
      return [oneDark];
    }
    return [
      EditorView.theme({
        "&": {
          color: "hsl(var(--foreground))",
          backgroundColor: "hsl(var(--background))",
        },
        ".cm-content": {
          padding: "16px",
          fontSize: "14px",
          fontFamily: "var(--font-mono)",
          minHeight: "200px",
        },
        ".cm-focused": {
          outline: "none",
        },
        ".cm-editor": {
          borderRadius: "0",
        },
        ".cm-scroller": {
          fontFamily: "var(--font-mono)",
        },
        ".cm-line": {
          lineHeight: "1.5",
        },
        "&.cm-focused .cm-cursor": {
          borderLeftColor: "hsl(var(--primary))",
        },
        "&.cm-focused .cm-selectionBackground, .cm-selectionBackground": {
          backgroundColor: "hsl(var(--primary) / 0.2)",
        },
      }),
    ];
  }, [isDarkMode]);

  // Editor panel
  const editorPanel = (
    <div className="h-full flex flex-col">
      <div className="flex-1 flex flex-col min-h-0">
        <Card className="flex-1 flex flex-col overflow-hidden rounded-none border-0">
          <CardHeader className="bg-muted/50 px-4 py-2 flex-shrink-0">
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-3">
                {/* Company field */}
                <CompanyLogo
                  companyName={selectedCompany}
                  variant="full"
                  size="md"
                  onClick={() => onCompanyClick(selectedCompany)}
                  data-testid="company-selector"
                />

                {/* Difficulty field */}
                <DifficultyBadge
                  difficulty={selectedDifficulty}
                  variant="full"
                  size="md"
                  showIcon={true}
                  showBars={true}
                  onClick={() => onDifficultyClick(selectedDifficulty)}
                  data-testid="difficulty-selector"
                />
              </div>

              <div className="flex items-center space-x-3">
                {/* Timer Unit - now comes first */}
                <div className="flex items-center space-x-1 px-2 py-1 rounded border bg-muted text-muted-foreground border-border">
                  {/* Play/Pause Toggle button - LEFT of timer */}
                  <Button
                    onClick={isTimerRunning ? pauseTimer : startTimer}
                    variant="ghost"
                    size="sm"
                    className={`h-5 w-5 p-0 hover:bg-transparent ${
                      isTimerRunning
                        ? "text-orange-600 dark:text-orange-400"
                        : "text-muted-foreground"
                    }`}
                    data-testid={
                      isTimerRunning
                        ? "button-pause-timer"
                        : "button-start-timer"
                    }
                    aria-label={isTimerRunning ? "Pause timer" : "Start timer"}
                  >
                    {isTimerRunning ? (
                      <Pause className="h-3 w-3" />
                    ) : (
                      <Play className="h-3 w-3" />
                    )}
                  </Button>

                  {/* Timer Display */}
                  <div className="flex items-center space-x-1">
                    <Timer
                      className={`h-3 w-3 ${
                        isTimerRunning
                          ? "text-orange-600 dark:text-orange-400"
                          : "text-muted-foreground"
                      }`}
                    />
                    <span
                      className={`font-mono text-xs ${
                        isTimerRunning
                          ? "text-orange-600 dark:text-orange-400 font-medium"
                          : "text-muted-foreground"
                      }`}
                      data-testid="text-timer"
                    >
                      {formatTimer(timerSeconds)}
                    </span>
                  </div>

                  {/* Reset button - RIGHT of timer */}
                  <Button
                    onClick={resetTimerOnly}
                    variant="ghost"
                    size="sm"
                    className={`h-5 w-5 p-0 hover:bg-transparent ${
                      isTimerRunning
                        ? "text-orange-600 dark:text-orange-400"
                        : "text-muted-foreground"
                    }`}
                    data-testid="button-reset-timer"
                    aria-label="Reset timer"
                  >
                    <Square className="h-3 w-3" />
                  </Button>
                </div>

                {/* Reactive PostgreSQL Database Dropdown - now comes after company/difficulty */}
                <DropdownMenu>
                  <DropdownMenuTrigger asChild>
                    <Button
                      variant="ghost"
                      size="sm"
                      className="h-7 px-2 text-xs text-muted-foreground hover:text-foreground border border-border bg-muted"
                      data-testid="button-db-selector"
                    >
                      <span>{selectedDatabase}</span>
                      <ChevronDown className="h-3 w-3 ml-1" />
                    </Button>
                  </DropdownMenuTrigger>
                  <DropdownMenuContent align="end" className="w-48">
                    <DropdownMenuItem
                      onClick={() => setSelectedDatabase("PostgreSQL 14")}
                      className={
                        selectedDatabase === "PostgreSQL 14" ? "bg-accent" : ""
                      }
                    >
                      PostgreSQL 14
                    </DropdownMenuItem>
                    <DropdownMenuItem
                      onClick={() => setSelectedDatabase("PostgreSQL 15")}
                      className={
                        selectedDatabase === "PostgreSQL 15" ? "bg-accent" : ""
                      }
                    >
                      PostgreSQL 15
                    </DropdownMenuItem>
                    <DropdownMenuItem
                      onClick={() => setSelectedDatabase("PostgreSQL 16")}
                      className={
                        selectedDatabase === "PostgreSQL 16" ? "bg-accent" : ""
                      }
                    >
                      PostgreSQL 16
                    </DropdownMenuItem>
                    <DropdownMenuItem
                      onClick={() => setSelectedDatabase("MySQL 8.0")}
                      className={
                        selectedDatabase === "MySQL 8.0" ? "bg-accent" : ""
                      }
                    >
                      MySQL 8.0
                    </DropdownMenuItem>
                    <DropdownMenuItem
                      onClick={() => setSelectedDatabase("SQLite")}
                      className={
                        selectedDatabase === "SQLite" ? "bg-accent" : ""
                      }
                    >
                      SQLite
                    </DropdownMenuItem>
                  </DropdownMenuContent>
                </DropdownMenu>
              </div>
            </div>
          </CardHeader>

          <CardContent className="p-0 flex-1 min-h-0 overflow-hidden">
            <CodeMirror
              value={query}
              onChange={(value) => setQuery(value)}
              height="calc(100vh - 200px)"
              theme={theme}
              extensions={extensions}
              basicSetup={{
                lineNumbers: true,
                foldGutter: true,
                dropCursor: false,
                allowMultipleSelections: false,
                indentOnInput: true,
                bracketMatching: true,
                closeBrackets: true,
                autocompletion: false,
                highlightSelectionMatches: false,
                searchKeymap: true,
                tabSize: 2,
              }}
              data-testid="editor-sql"
              className="sqlgym-editor"
            />
          </CardContent>
        </Card>
      </div>

      {/* Action Buttons */}
      <div className="flex-shrink-0 p-2 bg-muted/30">
        <div className="flex justify-end gap-3">
          <Button
            onClick={handleRun}
            disabled={isRunning || !query.trim()}
            className="bg-primary text-primary-foreground hover:bg-primary/90 font-semibold h-8"
            data-testid="button-run-query"
          >
            <Play className="mr-2 h-4 w-4" />
            {isRunning ? "Running..." : "Run Code"}
          </Button>

          <Button
            onClick={handleSubmit}
            disabled={isSubmitting || !query.trim()}
            className="bg-green-600 hover:bg-green-700 text-white font-semibold h-8"
            data-testid="button-submit"
          >
            <Save className="mr-2 h-4 w-4" />
            {isSubmitting ? "Submitting..." : "Check Solution"}
          </Button>
        </div>
      </div>
    </div>
  );

  // Output panel
  const outputPanel = (
    <Card className="h-full rounded-none border-0">
      <CardHeader className="bg-muted/50 px-5 py-2.5 border-b border-border">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-2.5">
            <TrendingUp className="h-3.5 w-3.5 text-primary" />
            <span className="font-semibold text-sm text-foreground">
              Output
            </span>
          </div>
          <div className="text-xs text-muted-foreground">
            {result?.executionTime &&
              `Execution time: ${result.executionTime || 0.01604} seconds`}
          </div>
        </div>
      </CardHeader>

      <CardContent className="p-5 h-full overflow-auto">
        {!result ? (
          <div className="flex flex-col items-center justify-center h-full text-center">
            <div className="text-3xl mb-3">⚡</div>
            <p className="text-sm text-muted-foreground">Ready to execute!</p>
            <p className="text-xs text-muted-foreground mt-1.5">
              Use Alt + Enter to run query
            </p>
          </div>
        ) : result.error ? (
          <div className="space-y-3">
            <div className="flex items-center space-x-2.5 text-red-600">
              <div className="w-2.5 h-2.5 bg-red-500 rounded-full"></div>
              <span className="font-medium text-sm">Query Failed</span>
            </div>
            <div className="bg-red-50 dark:bg-red-950/30 border border-red-200 dark:border-red-800 rounded-lg p-3">
              <p className="text-red-800 dark:text-red-200 text-xs font-mono">
                {result.message}
              </p>
            </div>
            <p className="text-xs text-muted-foreground">
              Check your query and try again.
            </p>
          </div>
        ) : (
          <div className="space-y-3">
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-2.5 text-green-600">
                <div className="w-2.5 h-2.5 bg-green-500 rounded-full"></div>
                <span className="font-medium text-sm">
                  {result.isCorrect ? "Perfect! 🏆" : "Query Complete"}
                </span>
              </div>
            </div>

            {result.isCorrect && (
              <div className="bg-green-50 dark:bg-green-950/30 border border-green-200 dark:border-green-800 rounded-lg p-3">
                <div className="flex items-center space-x-1.5">
                  <span className="text-lg">🎉</span>
                  <div>
                    <p className="text-green-800 dark:text-green-200 font-medium text-sm">
                      Excellent work!
                    </p>
                    <p className="text-green-700 dark:text-green-300 text-xs">
                      Your solution is correct!
                    </p>
                  </div>
                </div>
              </div>
            )}

            <div className="bg-muted/50 rounded-lg p-3">
              <p className="text-xs text-muted-foreground mb-1.5">
                📊 Query Results:
              </p>
              <div className="font-mono text-xs bg-background rounded border p-2 overflow-x-auto">
                <p>
                  Status: {result.isCorrect ? "✅ Correct" : "⚠️ Review needed"}
                </p>
                <p className="text-muted-foreground mt-1.5">
                  [Table data would be displayed here]
                </p>
              </div>
            </div>
          </div>
        )}
      </CardContent>
    </Card>
  );

  // Show resizable layout when output is visible, otherwise show just the editor
  if (showOutput) {
    return (
      <VerticalResizableSplitter
        topPanel={editorPanel}
        bottomPanel={outputPanel}
        defaultTopHeight={60}
        minTopHeight={35}
        minBottomHeight={25}
        className="h-full"
      />
    );
  }

  // Show just the editor when no output
  return <div className="h-full">{editorPanel}</div>;
}

export default function ProblemDetail() {
  const params = useParams();
  const problemId = params.id as string;
  const [, setLocation] = useLocation();
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [showHint, setShowHint] = useState(false);
  const [hintIndex, setHintIndex] = useState(0);

  // Reset hint state when problem changes
  useEffect(() => {
    setShowHint(false);
    setHintIndex(0);
  }, [problemId]);

  // Navigation handlers for filtering
  const handleDifficultyClick = useCallback((difficulty: string) => {
    setLocation(`/problems?difficulty=${encodeURIComponent(difficulty)}`);
  }, [setLocation]);

  const handleCompanyClick = useCallback((company: string) => {
    setLocation(`/problems?company=${encodeURIComponent(company)}`);
  }, [setLocation]);

  const { data: problem, isLoading: problemLoading } = useQuery({
    queryKey: ["/api/problems", problemId],
    queryFn: () => problemsApi.getById(problemId),
    enabled: !!problemId,
  });

  const { data: userSubmissions } = useQuery({
    queryKey: ["/api/submissions/user", user?.id, problemId],
    queryFn: () => submissionsApi.getUserSubmissions(user!.id),
    enabled: !!user?.id,
    select: (submissions) =>
      submissions.filter((s) => s.problemId === problemId),
  });

  const submitMutation = useMutation({
    mutationFn: (query: string) => submissionsApi.create({ problemId, query }),
    onSuccess: (result) => {
      toast({
        title: result.isCorrect ? "Success!" : "Query Executed",
        description: result.message,
        variant: result.isCorrect ? "default" : "destructive",
      });

      // Invalidate relevant queries
      queryClient.invalidateQueries({ queryKey: ["/api/submissions/user"] });
      if (result.isCorrect) {
        queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
        queryClient.invalidateQueries({
          queryKey: ["/api/problems", problemId],
        });
      }
    },
    onError: (error) => {
      toast({
        title: "Submission failed",
        description:
          error instanceof Error ? error.message : "Please try again.",
        variant: "destructive",
      });
    },
  });

  // Run Code - Non-persistent evaluation (no auth required)
  const handleRunQuery = async (query: string) => {
    // For now, return a mock evaluation result
    // In a real app, this would call a separate evaluation endpoint
    await new Promise((resolve) => setTimeout(resolve, 800)); // Simulate processing
    return {
      isCorrect: false,
      message:
        'Query executed successfully. Use "Check Solution" to submit your final answer.',
      executionTime: Math.floor(Math.random() * 50) + 10,
    };
  };

  // Check Solution - Persistent submission (auth required)
  const handleSubmitSolution = async (query: string) => {
    if (!user) {
      toast({
        title: "Authentication required",
        description: "Please log in to submit solutions.",
        variant: "destructive",
      });
      throw new Error("Authentication required");
    }

    return submitMutation.mutateAsync(query);
  };

  if (problemLoading) {
    return (
      <div className="min-h-screen bg-background">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <div className="animate-pulse space-y-8">
            <div className="h-8 bg-muted rounded w-1/4" />
            <div className="grid lg:grid-cols-2 gap-8">
              <div className="space-y-6">
                <div className="h-6 bg-muted rounded w-3/4" />
                <div className="h-4 bg-muted rounded" />
                <div className="h-4 bg-muted rounded w-5/6" />
                <div className="h-32 bg-muted rounded" />
              </div>
              <div className="space-y-6">
                <div className="h-6 bg-muted rounded w-1/2" />
                <div className="h-64 bg-muted rounded" />
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (!problem) {
    return (
      <div className="min-h-screen bg-background">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <div className="text-center py-12">
            <h1 className="text-2xl font-bold text-foreground mb-4">
              Problem not found
            </h1>
            <p className="text-muted-foreground mb-6">
              The problem you're looking for doesn't exist.
            </p>
            <Link href="/problems">
              <Button data-testid="button-back-to-problems">
                <ArrowLeft className="mr-2 h-4 w-4" />
                Back to Problems
              </Button>
            </Link>
          </div>
        </div>
      </div>
    );
  }

  const getDifficultyColor = (difficulty: string) => {
    switch (difficulty) {
      case "Easy":
        return "bg-green-100 text-green-800";
      case "Medium":
        return "bg-yellow-100 text-yellow-800";
      case "Hard":
        return "bg-red-100 text-red-800";
      default:
        return "bg-gray-100 text-gray-800";
    }
  };

  const hasCorrectSubmission =
    userSubmissions?.some((s) => s.isCorrect) || false;

  const handleHintClick = () => {
    if (!showHint) {
      setShowHint(true);
    } else if (problem?.hints && hintIndex < problem.hints.length - 1) {
      setHintIndex(hintIndex + 1);
    }
  };

  return (
    <div className="h-screen bg-background flex flex-col">
      {/* Simplified Header with Logo and Back Button */}
      <div className="flex-shrink-0 bg-background border-b border-border px-4 py-3">
        <div className="flex items-center space-x-4">
          <Link
            href="/"
            className="flex items-center space-x-2"
            data-testid="link-home"
          >
            <Dumbbell className="text-primary text-xl" />
            <span className="text-xl font-bold text-foreground">SQLGym</span>
          </Link>

          <Link href="/problems">
            <Button
              variant="ghost"
              size="sm"
              className="h-8 px-3 text-sm"
              data-testid="button-back"
            >
              <ArrowLeft className="mr-2 h-4 w-4" />
              Back to Questions
            </Button>
          </Link>
        </div>
      </div>

      {/* Main Content Area */}
      <div className="flex-1 min-h-0">
        <ResizableSplitter
          defaultLeftWidth={45}
          minLeftWidth={30}
          minRightWidth={30}
          className="h-full"
          leftPanel={
            /* Problem Panel with Tabs - Full Height */
            <div className="h-full flex flex-col overflow-hidden bg-background">
              <Tabs
                defaultValue="question"
                className="flex-1 flex flex-col h-full"
              >
                <TabsList className="grid grid-cols-4 m-0 rounded-none border-b bg-muted/30">
                  <TabsTrigger
                    value="question"
                    className="flex items-center gap-2"
                    data-testid="tab-question"
                  >
                    <FileText className="h-4 w-4" />
                    Question
                  </TabsTrigger>
                  <TabsTrigger
                    value="solution"
                    className="flex items-center gap-2"
                    data-testid="tab-solution"
                  >
                    <Code2 className="h-4 w-4" />
                    Solution
                  </TabsTrigger>
                  <TabsTrigger
                    value="discussion"
                    className="flex items-center gap-2"
                    data-testid="tab-discussion"
                  >
                    <MessageSquare className="h-4 w-4" />
                    Discussion
                  </TabsTrigger>
                  <TabsTrigger
                    value="submission"
                    className="flex items-center gap-2"
                    data-testid="tab-submission"
                  >
                    <CheckCircle className="h-4 w-4" />
                    Submission
                  </TabsTrigger>
                </TabsList>

                <TabsContent
                  value="question"
                  className="flex-1 overflow-y-auto overflow-x-hidden p-6 mt-0 min-h-0"
                  data-testid="content-question"
                >
                  <div className="space-y-6 pb-8">
                    {/* Title Header - Now scrollable */}
                    <div className="pb-3 border-b">
                      <div className="flex items-center justify-between">
                        <div className="flex items-center space-x-3">
                          <h1
                            className="text-lg font-bold text-foreground"
                            data-testid="text-problem-title"
                          >
                            {problem?.title}
                          </h1>
                          {hasCorrectSubmission && (
                            <Badge className="bg-green-100 text-green-800 text-xs px-2 py-1">
                              ✓ Solved
                            </Badge>
                          )}
                        </div>
                      </div>
                    </div>

                    {/* Problem Description */}
                    <div>
                      <div
                        className="text-foreground leading-relaxed mb-6 prose prose-sm max-w-none break-words"
                        data-testid="text-problem-description"
                      >
                        <ReactMarkdown
                          remarkPlugins={[remarkGfm]}
                          components={{
                            h1: ({ children }) => (
                              <h1 className="text-2xl font-bold mb-4">
                                {children}
                              </h1>
                            ),
                            h2: ({ children }) => (
                              <h2 className="text-xl font-semibold mb-3">
                                {children}
                              </h2>
                            ),
                            h3: ({ children }) => (
                              <h3 className="text-lg font-medium mb-2">
                                {children}
                              </h3>
                            ),
                            p: ({ children }) => (
                              <p className="mb-4 leading-relaxed">{children}</p>
                            ),
                            ul: ({ children }) => (
                              <ul className="list-disc list-inside mb-4 space-y-1">
                                {children}
                              </ul>
                            ),
                            ol: ({ children }) => (
                              <ol className="list-decimal list-inside mb-4 space-y-1">
                                {children}
                              </ol>
                            ),
                            li: ({ children }) => (
                              <li className="text-foreground">{children}</li>
                            ),
                            code: ({ children, className }) => {
                              const isInline = !className;
                              if (isInline) {
                                return (
                                  <code className="bg-muted px-1.5 py-0.5 rounded text-sm font-mono text-foreground">
                                    {children}
                                  </code>
                                );
                              }
                              return (
                                <code className="bg-muted text-sm font-mono block">
                                  {children}
                                </code>
                              );
                            },
                            pre: ({ children }) => (
                              <pre className="bg-muted p-4 rounded-lg overflow-x-auto mb-4 text-sm font-mono">
                                {children}
                              </pre>
                            ),
                            blockquote: ({ children }) => (
                              <blockquote className="border-l-4 border-primary bg-muted/50 pl-4 pr-4 py-3 my-4 rounded-r-lg">
                                {children}
                              </blockquote>
                            ),
                            strong: ({ children }) => (
                              <strong className="font-semibold text-foreground">
                                {children}
                              </strong>
                            ),
                            em: ({ children }) => (
                              <em className="italic text-muted-foreground">
                                {children}
                              </em>
                            ),
                            table: ({ children }) => (
                              <div className="overflow-x-auto my-4">
                                <table className="min-w-full border-collapse border border-muted">
                                  {children}
                                </table>
                              </div>
                            ),
                            thead: ({ children }) => (
                              <thead className="bg-muted/50">{children}</thead>
                            ),
                            tbody: ({ children }) => <tbody>{children}</tbody>,
                            tr: ({ children }) => (
                              <tr className="border-b border-muted">
                                {children}
                              </tr>
                            ),
                            th: ({ children }) => (
                              <th className="border border-muted px-3 py-2 text-left font-semibold">
                                {children}
                              </th>
                            ),
                            td: ({ children }) => (
                              <td className="border border-muted px-3 py-2">
                                {children}
                              </td>
                            ),
                          }}
                        >
                          {problem.question?.description || ""}
                        </ReactMarkdown>
                      </div>
                    </div>

                    {/* Structured Table Display */}
                    <TableDisplay
                      tables={problem.question?.tables || []}
                      expectedOutput={problem.question?.expectedOutput || []}
                    />

                    {/* Hints Section */}
                    {problem?.hints && problem.hints.length > 0 && (
                      <div className="space-y-3">
                        {!showHint ? (
                          <Button
                            onClick={handleHintClick}
                            variant="outline"
                            className="w-full text-primary hover:bg-primary/10"
                            data-testid="button-hint"
                          >
                            <Lightbulb className="mr-2 h-4 w-4" />
                            HINT
                          </Button>
                        ) : (
                          <>
                            <div className="space-y-4">
                              {problem?.hints
                                .slice(0, hintIndex + 1)
                                .map((hint: string, index: number) => {
                                  // Parse hint content and extract code blocks
                                  const parseHintContent = (
                                    content: string
                                  ) => {
                                    const parts = content.split(
                                      /(\*\/\*[\s\S]*?\*\/\*)/
                                    );
                                    return parts.map((part, partIndex) => {
                                      if (
                                        part.startsWith("*/*") &&
                                        part.endsWith("*/*")
                                      ) {
                                        // Extract code content between */* and */*
                                        const codeContent = part.slice(3, -3);
                                        return (
                                          <code
                                            key={partIndex}
                                            className="block bg-gray-100 dark:bg-gray-800 p-3 rounded-md text-sm font-mono my-2 whitespace-pre-wrap"
                                          >
                                            {codeContent}
                                          </code>
                                        );
                                      } else {
                                        return part;
                                      }
                                    });
                                  };

                                  return (
                                    <div
                                      key={index}
                                      className="text-foreground"
                                    >
                                      <h3 className="text-center font-bold text-lg mb-2 text-foreground">
                                        HINT {index + 1}
                                      </h3>
                                      <p className="text-foreground leading-relaxed">
                                        {parseHintContent(hint)}
                                      </p>
                                    </div>
                                  );
                                })}
                            </div>

                            {hintIndex < (problem?.hints?.length || 0) - 1 && (
                              <Button
                                onClick={handleHintClick}
                                variant="outline"
                                className="w-full text-primary hover:bg-primary/10 mt-3"
                                data-testid="button-hint"
                              >
                                <Lightbulb className="mr-2 h-4 w-4" />
                                HINT
                              </Button>
                            )}
                          </>
                        )}
                      </div>
                    )}

                    {/* Tags */}
                    {problem?.tags && problem.tags.length > 0 && (
                      <div>
                        <h4 className="text-sm font-medium text-foreground mb-2">
                          Tags
                        </h4>
                        <div className="flex flex-wrap gap-2">
                          {problem?.tags.map((tag: string, index: number) => (
                            <Badge
                              key={index}
                              variant="outline"
                              data-testid={`tag-${tag}`}
                            >
                              {tag}
                            </Badge>
                          ))}
                        </div>
                      </div>
                    )}
                  </div>
                </TabsContent>

                <TabsContent
                  value="solution"
                  className="flex-1 overflow-auto p-6 pt-0 mt-0"
                  data-testid="content-solution"
                >
                  <div className="space-y-6">
                    <div className="text-center py-8">
                      <Code2 className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
                      <h3 className="text-lg font-semibold text-foreground mb-2">
                        Solution
                      </h3>
                      <p className="text-muted-foreground mb-4">
                        Complete this problem to view the official solution and
                        explanations.
                      </p>
                      {!hasCorrectSubmission && (
                        <Alert className="border-yellow-200 bg-yellow-50 dark:bg-yellow-950/30">
                          <AlertDescription className="text-yellow-800 dark:text-yellow-200">
                            💡 Solve the problem first to unlock the detailed
                            solution walkthrough!
                          </AlertDescription>
                        </Alert>
                      )}
                      {hasCorrectSubmission && (
                        <div className="mt-6 p-6 bg-muted/50 rounded-lg">
                          <h4 className="font-semibold mb-4">
                            Official Solution:
                          </h4>
                          <div className="text-left font-mono text-sm bg-background rounded border p-4">
                            <p className="text-muted-foreground">
                              [Solution code would be displayed here]
                            </p>
                          </div>
                        </div>
                      )}
                    </div>
                  </div>
                </TabsContent>

                <TabsContent
                  value="discussion"
                  className="flex-1 overflow-auto p-6 pt-0 mt-0"
                  data-testid="content-discussion"
                >
                  <div className="space-y-6">
                    <div className="flex items-center justify-between">
                      <h3 className="text-lg font-semibold text-foreground">
                        Discussion
                      </h3>
                      <Button
                        variant="outline"
                        size="sm"
                        data-testid="button-new-discussion"
                      >
                        <MessageSquare className="h-4 w-4 mr-2" />
                        New Discussion
                      </Button>
                    </div>

                    <div className="space-y-4">
                      <div className="text-center py-8">
                        <MessageSquare className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
                        <h4 className="text-base font-semibold text-foreground mb-2">
                          No discussions yet
                        </h4>
                        <p className="text-muted-foreground mb-4">
                          Be the first to start a discussion about this problem!
                        </p>
                      </div>
                    </div>
                  </div>
                </TabsContent>

                <TabsContent
                  value="submission"
                  className="flex-1 overflow-auto p-6 pt-0 mt-0"
                  data-testid="content-submission"
                >
                  <div className="space-y-6">
                    <div className="flex items-center justify-between">
                      <h3 className="text-lg font-semibold text-foreground">
                        My Submissions
                      </h3>
                      <div className="text-sm text-muted-foreground">
                        {userSubmissions?.length || 0} submissions
                      </div>
                    </div>

                    {userSubmissions && userSubmissions.length > 0 ? (
                      <div className="space-y-3">
                        {userSubmissions.map((submission, index) => (
                          <Card key={submission.id} className="p-4">
                            <div className="flex items-center justify-between">
                              <div className="flex items-center space-x-3">
                                <div
                                  className={`w-3 h-3 rounded-full ${
                                    submission.isCorrect
                                      ? "bg-green-500"
                                      : "bg-red-500"
                                  }`}
                                />
                                <span className="text-sm font-mono">
                                  Submission {index + 1}
                                </span>
                                {submission.isCorrect && (
                                  <Badge className="bg-green-100 text-green-800 text-xs">
                                    ✓ Accepted
                                  </Badge>
                                )}
                              </div>
                              <div className="text-xs text-muted-foreground">
                                {new Date(
                                  submission.submittedAt
                                ).toLocaleDateString()}
                              </div>
                            </div>
                            <div className="mt-3 text-sm text-muted-foreground">
                              Runtime: {submission.executionTime || "N/A"}ms
                            </div>
                          </Card>
                        ))}
                      </div>
                    ) : (
                      <div className="text-center py-8">
                        <CheckCircle className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
                        <h4 className="text-base font-semibold text-foreground mb-2">
                          No submissions yet
                        </h4>
                        <p className="text-muted-foreground mb-4">
                          Submit your first solution to see it here!
                        </p>
                      </div>
                    )}
                  </div>
                </TabsContent>
              </Tabs>
            </div>
          }
          rightPanel={
            /* Editor + Output Panels with Vertical Split */
            <EditorOutputSplit
              problem={problem}
              handleRunQuery={handleRunQuery}
              handleSubmitSolution={handleSubmitSolution}
              onDifficultyClick={handleDifficultyClick}
              onCompanyClick={handleCompanyClick}
            />
          }
        />
      </div>
    </div>
  );
}

