/* ===== CSS ANIMATIONS & STYLES ===== */

/* Base animations and keyframes */
@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(10px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes pulse {
  0%,
  100% {
    transform: scale(1);
    opacity: 0.8;
  }
  50% {
    transform: scale(1.2);
    opacity: 1;
  }
}

@keyframes bounce {
  0%,
  100% {
    transform: translateY(0);
  }
  50% {
    transform: translateY(-2px);
  }
}

@keyframes skillBarGrow {
  from {
    transform: scaleY(0);
  }
  to {
    transform: scaleY(1);
  }
}

@keyframes shimmer {
  0% {
    background-position: -200px 0;
  }
  100% {
    background-position: 200px 0;
  }
}

/* ===== COMPANY FIELD STYLES ===== */
.company-field {
  display: inline-flex;
  align-items: center;
  gap: 6px;
  padding: 6px 10px;
  font-size: 12px;
  font-weight: 500;
  border-radius: 6px;
  background: white;
  color: #374151;
  border: none;
  cursor: pointer;
  transition: all 0.2s cubic-bezier(0.4, 0, 0.2, 1);
  position: relative;
  overflow: hidden;
  animation: fadeInUp 0.3s ease-out;
}

/* Dark mode */
@media (prefers-color-scheme: dark) {
  .company-field {
    background: #1f2937;
    color: #d1d5db;
  }
}

/* Hover effects */
.company-field:hover {
  transform: translateY(-1px);
  background: #f3f4f6;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
}

@media (prefers-color-scheme: dark) {
  .company-field:hover {
    background: #374151;
  }
}

/* Selected state */
.company-field.selected {
  background: #dbeafe;
  color: #1d4ed8;
  box-shadow: 0 1px 3px rgba(59, 130, 246, 0.2);
}

