  hints?: string[];
  tags?: string[];
}

interface ProblemDescriptionTabProps {
  problem?: Problem;
  className?: string;
  problemId?: string;
}

const ProblemDescriptionTab = memo(function ProblemDescriptionTab({
  problem,
  className,
  problemId,
}: ProblemDescriptionTabProps) {
  const [showHint, setShowHint] = useState(false);
  const [hintIndex, setHintIndex] = useState(0);

  // Force cache invalidation on mount to ensure fresh data
  useEffect(() => {
    if (problemId) {
      queryClient.invalidateQueries({ queryKey: ["/api/problems", problemId] });
    }
  }, [problemId]);

  const handleHintClick = useCallback(() => {
    if (!showHint) {
      setShowHint(true);
    } else if (hintIndex < (problem?.hints?.length || 0) - 1) {
      setHintIndex((prev) => prev + 1);
    }
  }, [showHint, hintIndex, problem?.hints?.length]);

  // Parse hint content and extract code blocks
  const parseHintContent = useCallback((content: string) => {
    const parts = content.split(/(\*\/\*[\s\S]*?\*\/\*)/);
    return parts.map((part, partIndex) => {
      if (part.startsWith("*/*") && part.endsWith("*/*")) {
        const codeContent = part.slice(3, -3);
        return (
          <code
            key={partIndex}
            className="block bg-gray-100 dark:bg-gray-800 p-3 rounded-md text-sm font-mono my-2 whitespace-pre-wrap"
          >
            {codeContent}
          </code>
        );
      } else {
        return part;
      }
    });
  }, []);

  return (
    <div className={`space-y-6 ${className || ""}`}>
      {/* Problem Description */}
      <div className="space-y-4">
        <div className="text-foreground prose prose-sm max-w-none dark:prose-invert">
          <ReactMarkdown
            remarkPlugins={[remarkGfm]}
            components={{
              p: ({ children }) => (
                <p className="mb-3 text-foreground leading-relaxed">
                  {children}
                </p>
              ),
              h1: ({ children }) => (
                <h1 className="text-xl font-bold mb-4 text-foreground">
                  {children}
                </h1>
              ),
              h2: ({ children }) => (
                <h2 className="text-lg font-semibold mb-3 text-foreground">
                  {children}
                </h2>
              ),
              h3: ({ children }) => (
                <h3 className="text-base font-semibold mb-2 text-foreground">
                  {children}
                </h3>
              ),
              ul: ({ children }) => (
                <ul className="list-disc list-inside mb-3 space-y-1 text-foreground">
                  {children}
                </ul>
              ),
              ol: ({ children }) => (
                <ol className="list-decimal list-inside mb-3 space-y-1 text-foreground">
                  {children}
                </ol>
              ),
              li: ({ children }) => (
                <li className="text-foreground">{children}</li>
              ),
              strong: ({ children }) => (
                <strong className="font-semibold text-foreground">
                  {children}
                </strong>
              ),
              em: ({ children }) => (
                <em className="italic text-foreground">{children}</em>
              ),
              code: ({ children }) => (
                <code className="bg-muted px-1.5 py-0.5 rounded text-sm font-mono text-foreground">
                  {children}
                </code>
              ),
              pre: ({ children }) => (
                <pre className="bg-muted p-4 rounded-lg overflow-x-auto text-sm font-mono text-foreground mb-4">
                  {children}
                </pre>
              ),
              blockquote: ({ children }) => (
                <blockquote className="border-l-4 border-primary pl-4 italic text-muted-foreground mb-4">
                  {children}
                </blockquote>
              ),
              table: ({ children }) => (
                <div className="overflow-x-auto mb-4">
                  <table className="min-w-full border border-muted rounded-lg">
                    {children}
                  </table>
                </div>
              ),
              thead: ({ children }) => (
                <thead className="bg-muted/50">{children}</thead>
              ),
              tbody: ({ children }) => <tbody>{children}</tbody>,
              tr: ({ children }) => (
                <tr className="border-b border-muted">{children}</tr>
              ),
              th: ({ children }) => (
                <th className="border border-muted px-3 py-2 text-left font-semibold">
                  {children}
                </th>
              ),
              td: ({ children }) => (
                <td className="border border-muted px-3 py-2">{children}</td>
              ),
            }}
          >
            {problem?.question?.description || ""}
          </ReactMarkdown>
        </div>
      </div>

      {/* Structured Table Display */}
      <TableDisplay
        tables={problem?.question?.tables || []}
        expectedOutput={problem?.expectedDisplay || problem?.question?.expectedOutput || []}
      />

      {/* Hints Section */}
      {problem?.hints && problem.hints.length > 0 && (
        <div className="space-y-3">
          {!showHint ? (
            <Button
              onClick={handleHintClick}
              variant="outline"
              className="w-full text-primary hover:bg-primary/10"
              data-testid="button-hint"
            >
              <Lightbulb className="mr-2 h-4 w-4" />
              HINT
            </Button>
          ) : (
            <>
              <div className="space-y-4">
                {problem.hints
                  .slice(0, hintIndex + 1)
                  .map((hint: string, index: number) => (
                    <div key={index} className="text-foreground">
                      <h3 className="text-center font-bold text-lg mb-2 text-foreground">
                        HINT {index + 1}
                      </h3>
                      <p className="text-foreground leading-relaxed">
                        {parseHintContent(hint)}
                      </p>
                    </div>
                  ))}
              </div>

              {hintIndex < (problem?.hints?.length || 0) - 1 && (
                <Button
                  onClick={handleHintClick}
                  variant="outline"
                  className="w-full text-primary hover:bg-primary/10 mt-3"
                  data-testid="button-hint"
                >
                  <Lightbulb className="mr-2 h-4 w-4" />
                  HINT
                </Button>
              )}
            </>
          )}
        </div>
      )}

      {/* Tags */}
      {problem?.tags && problem.tags.length > 0 && (
        <div>
          <h4 className="text-sm font-medium text-foreground mb-2">Tags</h4>
          <div className="flex flex-wrap gap-2">
            {problem.tags.map((tag: string, index: number) => (
              <Badge key={index} variant="outline" data-testid={`tag-${tag}`}>
                {tag}
              </Badge>
            ))}
          </div>
        </div>
      )}
    </div>
  );
});


export default ProblemDescriptionTab;

client/src/components/ProblemNavigation..content:
import { memo, useCallback } from 'react';
import { ArrowLeft, ChevronLeft, ChevronRight, Users, Star, Lock, Bookmark, ThumbsUp, ThumbsDown } from 'lucide-react';
import { Link } from 'wouter';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { useMutation, useQueryClient } from '@tanstack/react-query';
import { problemsApi } from '@/lib/auth';
import { useAuth } from '@/hooks/use-auth';
import { useToast } from '@/hooks/use-toast';

interface Problem {
  id?: string;
  title?: string;
  likes?: number; // For backward compatibility
  upvotesCount?: number;
  company?: string;
  difficulty?: string;
  premium?: boolean | null;
  isBookmarked?: boolean;
  isLiked?: boolean; // For backward compatibility
  isUpvoted?: boolean;
  isDownvoted?: boolean;
}

interface ProblemNavigationProps {
  problem?: Problem;
  userSubmissions?: any[];
  onPrevious?: () => void;
  onNext?: () => void;
  hasPrevious?: boolean;
  hasNext?: boolean;
  className?: string;
}

const ProblemNavigation = memo(function ProblemNavigation({
  problem,
  userSubmissions = [],
  onPrevious,
  onNext,
  hasPrevious = false,
  hasNext = false,
  className,
}: ProblemNavigationProps) {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Memoized calculation for solved status
  const isSolved = useCallback(() => {
    return userSubmissions?.some((sub) => sub.isCorrect) || false;
  }, [userSubmissions]);

  // Bookmark mutation
  const bookmarkMutation = useMutation({
    mutationFn: async () => {
      if (!problem?.id) throw new Error("No problem ID");
      return problemsApi.toggleBookmark(problem.id);
    },
    onSuccess: () => {
      // Invalidate and refetch problem data to get updated bookmark status
      queryClient.invalidateQueries({
        queryKey: ["/api/problems", problem?.id],
      });
      toast({
        title: problem?.isBookmarked ? "Bookmark removed" : "Problem bookmarked",
        description: problem?.isBookmarked 
          ? "Problem removed from your bookmarks" 
          : "Problem added to your bookmarks",
      });
    },
    onError: (error) => {
      toast({
        title: "Failed to update bookmark",
        description: error instanceof Error ? error.message : "Unknown error",
        variant: "destructive",
      });
    },
  });

  // Upvote mutation
  const upvoteMutation = useMutation({
    mutationFn: async () => {
      if (!problem?.id) throw new Error("No problem ID");
      return problemsApi.toggleUpvote(problem.id);
    },
    onSuccess: () => {
      // Invalidate and refetch problem data to get updated vote status
      queryClient.invalidateQueries({
        queryKey: ["/api/problems", problem?.id],
      });
      toast({
        title: problem?.isUpvoted ? "Upvote removed" : "Problem upvoted",
        description: problem?.isUpvoted 
          ? "You removed your upvote from this problem" 
          : "You upvoted this problem",
      });
    },
    onError: (error) => {
      toast({
        title: "Failed to update upvote",
        description: error instanceof Error ? error.message : "Unknown error",
        variant: "destructive",
      });
    },
  });

  // Downvote mutation
  const downvoteMutation = useMutation({
    mutationFn: async () => {
      if (!problem?.id) throw new Error("No problem ID");
      return problemsApi.toggleDownvote(problem.id);
    },
    onSuccess: () => {
      // Invalidate and refetch problem data to get updated vote status
      queryClient.invalidateQueries({
        queryKey: ["/api/problems", problem?.id],
      });
      toast({
        title: problem?.isDownvoted ? "Downvote removed" : "Problem downvoted",
        description: problem?.isDownvoted 
          ? "You removed your downvote from this problem" 
          : "You downvoted this problem",
      });
    },
    onError: (error) => {
      toast({
        title: "Failed to update downvote",
        description: error instanceof Error ? error.message : "Unknown error",
        variant: "destructive",
      });
    },
  });

  const handlePrevious = useCallback(() => {
    if (hasPrevious && onPrevious) {
      onPrevious();
    }
  }, [hasPrevious, onPrevious]);

  const handleNext = useCallback(() => {
    if (hasNext && onNext) {
      onNext();
    }
  }, [hasNext, onNext]);

  return (
    <div className={`flex items-center justify-between py-4 px-6 border-b ${className || ''}`}>
      {/* Left: Back to Problems + Title */}
      <div className="flex items-center space-x-4">
        <Link to="/problems">
          <Button
            variant="ghost"
            size="sm"
            className="text-muted-foreground hover:text-foreground"
            data-testid="button-back-to-problems"
          >
            <ArrowLeft className="h-4 w-4 mr-2" />
            Problems
          </Button>
        </Link>

        {problem && (
          <>
            <div className="h-4 w-px bg-border"></div>
            <div className="flex items-center space-x-3">
              <div className="flex items-center gap-2">
                {problem.premium && (
                  <Lock className="w-4 h-4 text-amber-500" />
                )}
                <h1 className="text-lg font-semibold" data-testid="text-problem-title">
                  {problem.title || 'Untitled Problem'}
                </h1>
              </div>
              {isSolved() && (
                <Badge variant="secondary" className="bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200">
                  ✓ Solved
                </Badge>
              )}
            </div>
          </>
        )}
      </div>

      {/* Right: Problem Stats + Bookmark/Like Actions + Navigation */}
      <div className="flex items-center space-x-4">
        {problem && user && (
          <>
            {/* Bookmark, Upvote and Downvote Buttons */}
            <div className="flex items-center space-x-2">
              <Button
                onClick={() => bookmarkMutation.mutate()}
                disabled={bookmarkMutation.isPending}
                variant="ghost"
                size="sm"
                className={`h-8 w-8 p-0 ${problem.isBookmarked ? 'text-green-700 dark:text-green-400' : 'text-muted-foreground'}`}
                data-testid="button-bookmark"
              >
                <Bookmark className={`h-4 w-4 ${problem.isBookmarked ? 'fill-current' : ''}`} />
              </Button>

              <div className="flex items-center space-x-1">
                <Button
                  onClick={() => upvoteMutation.mutate()}
                  disabled={upvoteMutation.isPending}
                  variant="ghost"
                  size="sm"
                  className={`h-8 w-8 p-0 ${problem.isUpvoted ? 'text-green-600 dark:text-green-400' : 'text-muted-foreground'}`}
                  data-testid="button-upvote"
                >
                  <ThumbsUp className={`h-4 w-4 ${problem.isUpvoted ? 'fill-current' : ''}`} />
                </Button>

                {/* Show upvote count */}
                {(problem.upvotesCount && problem.upvotesCount > 0) && (
                  <span className="text-sm text-muted-foreground min-w-[1rem]">
                    {problem.upvotesCount}
                  </span>
                )}

                <Button
                  onClick={() => downvoteMutation.mutate()}
                  disabled={downvoteMutation.isPending}
                  variant="ghost"
                  size="sm"
                  className={`h-8 w-8 p-0 ${problem.isDownvoted ? 'text-red-600 dark:text-red-400' : 'text-muted-foreground'}`}
                  data-testid="button-downvote"
                >
                  <ThumbsDown className={`h-4 w-4 ${problem.isDownvoted ? 'fill-current' : ''}`} />
                </Button>
              </div>
            </div>

            {/* Problem Stats */}
            <div className="flex items-center space-x-3 text-sm text-muted-foreground">
              <div className="flex items-center space-x-1">
                <Users className="h-3 w-3" />
                <span>2.1k</span>
              </div>
              <div className="flex items-center space-x-1">
                <ThumbsUp className="h-3 w-3" />
                <span>{problem.upvotesCount || problem.likes || 0}</span>
              </div>
            </div>

            <div className="h-4 w-px bg-border"></div>
          </>
        )}

        {problem && !user && (
          <>
            {/* Problem Stats for non-logged in users */}
            <div className="flex items-center space-x-3 text-sm text-muted-foreground">
              <div className="flex items-center space-x-1">
                <Users className="h-3 w-3" />
                <span>2.1k</span>
              </div>
              <div className="flex items-center space-x-1">
                <ThumbsUp className="h-3 w-3" />
                <span>{problem.upvotesCount || problem.likes || 0}</span>
              </div>
            </div>

            <div className="h-4 w-px bg-border"></div>
          </>
        )}

        {/* Navigation Controls */}
        <div className="flex items-center space-x-2">
          <Button
            onClick={handlePrevious}
            disabled={!hasPrevious}
            variant="outline"
            size="sm"
            className="h-8 w-8 p-0"
            data-testid="button-previous-problem"
          >
            <ChevronLeft className="h-4 w-4" />
          </Button>
          <Button
            onClick={handleNext}
            disabled={!hasNext}
            variant="outline"
            size="sm"
            className="h-8 w-8 p-0"
            data-testid="button-next-problem"
          >
            <ChevronRight className="h-4 w-4" />
          </Button>
        </div>
      </div>
    </div>
  );
});

export default ProblemNavigation;

client/src/components/ProblemTabsContent.content:
import { memo, useState } from 'react';
import { Code2, MessageSquare, CheckCircle, BookOpen, Heart, Reply, Send, ChevronUp, ChevronDown, Lock } from 'lucide-react';
import { useQuery, useMutation } from "@tanstack/react-query";
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Button } from '@/components/ui/button';
import { Card, CardContent } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Textarea } from '@/components/ui/textarea';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { useToast } from '@/hooks/use-toast';
import { useAuth } from '@/hooks/use-auth';
import { apiRequest, queryClient } from '@/lib/queryClient';
import ProblemDescriptionTab from '@/components/ProblemDescriptionTab';
import AnswersScreen from '@/components/AnswersScreen';

interface Problem {
  id?: string;
  question?: {
    description?: string;
    tables?: any[];
    expectedOutput?: any[];
  };
  hints?: string[];
  tags?: string[];
  premium?: boolean | null;
}

interface Solution {
  id: string;
  problem_id: string;
  title: string;
  explanation: string;
  code: string;
  approach: string;
  time_complexity: string;
  space_complexity: string;
  tags: string[];
  created_at: string;
}

interface Submission {
  id: string;
  isCorrect: boolean;
  submittedAt: string;
  executionTime?: number;
}

interface TestResult {
  test_case_id: string;
  test_case_name: string;
  is_hidden: boolean;
  is_correct: boolean;
  score: number;
  feedback: string[];
  execution_time_ms: number;
  execution_status: string;
  validation_details: any;
  user_output: any[];
  expected_output: any[];
  output_matches: boolean;
}

interface SubmissionResult {
  success: boolean;
  is_correct: boolean;
  score: number;
  feedback: string[];
  test_results: TestResult[];
  submission_id: string;
  execution_stats: {
    avg_time_ms: number;
    max_time_ms: number;
    memory_used_mb: number;
  };
}

interface ProblemTabsContentProps {
  problem?: Problem;
  userSubmissions?: Submission[];
  latestSubmissionResult?: SubmissionResult | null;
  className?: string;
  activeTab?: string;
  onTabChange?: (value: string) => void;
  problemId?: string;
}

interface User {
  id: string;
  username: string;
  profileImageUrl?: string;
}

interface Discussion {
  id: string;
  userId: string;
  problemId: string;
  content: string;
  codeSnippet?: string;
  likes: number;
  comments: number;
  createdAt: string;
  user: User;
  isLiked?: boolean;
}

interface Comment {
  id: string;
  postId: string;
  userId: string;
  parentId?: string;
  content: string;
  createdAt: string;
  user: User;
  replies?: Comment[];
}

const NestedComment = memo(function NestedComment({
  comment,
  discussionId,
  onReply,
  depth = 0
}: {
  comment: Comment;
  discussionId: string;
  onReply: (content: string, parentId?: string) => void;
  depth: number;
}) {
  const [showReplyBox, setShowReplyBox] = useState(false);
  const [replyContent, setReplyContent] = useState('');
  const { toast } = useToast();

  const replyMutation = useMutation({
    mutationFn: async (data: { content: string; parentId?: string }) => {
      return apiRequest('POST', `/api/community/posts/${discussionId}/comments`, { 
          content: data.content,
          parent_id: data.parentId 
        });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/community/posts/${discussionId}/comments`] });
      setReplyContent('');
      setShowReplyBox(false);
      toast({ title: "Reply posted successfully!" });
    },
    onError: () => {
      toast({ title: "Failed to post reply", variant: "destructive" });
    },
  });

  const handleSubmitReply = () => {
    if (!replyContent.trim()) return;
    replyMutation.mutate({ content: replyContent, parentId: comment.id });
  };

  const maxDepth = 3; // Limit nesting depth to avoid infinite nesting
  const isMaxDepth = depth >= maxDepth;

  return (
    <div className={`${depth > 0 ? 'ml-6 mt-3' : ''}`} data-testid={`comment-${comment.id}`}>
      <div className="flex items-start space-x-3">
        <Avatar className={`${depth > 0 ? 'w-6 h-6' : 'w-8 h-8'}`}>
          <AvatarImage src={comment.user.profileImageUrl} alt={comment.user.username} />
          <AvatarFallback>
            {comment.user.username?.charAt(0).toUpperCase() || 'U'}
          </AvatarFallback>
        </Avatar>

        <div className="flex-1">
          <div className="flex items-center space-x-2 mb-1">
            <span className={`font-medium text-foreground ${depth > 0 ? 'text-sm' : 'text-sm'}`}>
              {comment.user.username}
            </span>
            <span className="text-xs text-muted-foreground">
              {new Date(comment.createdAt).toLocaleDateString()}
            </span>
          </div>

          <p className={`text-foreground whitespace-pre-wrap ${depth > 0 ? 'text-sm' : 'text-sm'}`}>
            {comment.content}
          </p>

          {/* Reply button */}
          {!isMaxDepth && (
            <div className="mt-2">
              <Button
                variant="ghost"
                size="sm"
                onClick={() => setShowReplyBox(!showReplyBox)}
                className="text-xs text-muted-foreground hover:text-blue-500 h-6 px-2"
                data-testid={`button-reply-${comment.id}`}
              >
                <Reply className="w-3 h-3 mr-1" />
                Reply
              </Button>
            </div>
          )}

          {/* Reply input box */}
          {showReplyBox && (
            <div className="mt-3 space-y-2">
              <Textarea
                placeholder={`Reply to ${comment.user.username}...`}
                value={replyContent}
                onChange={(e) => setReplyContent(e.target.value)}
                rows={2}
                className="resize-none text-sm"
                data-testid={`textarea-nested-reply-${comment.id}`}
              />
              <div className="flex justify-end space-x-2">
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => {
                    setShowReplyBox(false);
                    setReplyContent('');
                  }}
                  className="h-8 text-xs"
                  data-testid={`button-cancel-reply-${comment.id}`}
                >
                  Cancel
                </Button>
                <Button
                  size="sm"
                  onClick={handleSubmitReply}
                  disabled={!replyContent.trim() || replyMutation.isPending}
                  className="h-8 text-xs"
                  data-testid={`button-submit-reply-${comment.id}`}
                >
                  {replyMutation.isPending ? "Posting..." : "Reply"}
                </Button>
              </div>
            </div>
          )}

          {/* Nested replies */}
          {comment.replies && comment.replies.length > 0 && (
            <div className="mt-3 space-y-3">
              {comment.replies.map((reply) => (
                <NestedComment
                  key={reply.id}
                  comment={reply}
                  discussionId={discussionId}
                  onReply={onReply}
                  depth={depth + 1}
                />
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
});

const OutputTable = ({ data, title }: { data: any[]; title: string }) => {
  if (!data || data.length === 0) {
    return (
      <div className="bg-gray-50 border border-gray-200 rounded-lg p-4">
        <h3 className="text-sm font-medium text-gray-700 mb-2">{title}</h3>
        <div className="text-gray-500 italic text-sm">No data to display</div>
      </div>
    );
  }

  const headers = Object.keys(data[0]);

  return (
    <div className="bg-white border border-gray-200 rounded-lg overflow-hidden">
      <div className="bg-gray-50 px-4 py-2 border-b border-gray-200">
        <h3 className="text-sm font-medium text-gray-700">{title}</h3>
      </div>
      <div className="overflow-auto max-h-80 min-h-32">
        <table className="w-full">
          <thead className="sticky top-0 bg-gray-50 z-10">
            <tr className="border-b border-gray-200">
              {headers.map((header, i) => (
                <th 
                  key={i} 
                  className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
                >
                  {header}
                </th>
              ))}
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-200">
            {data.map((row, i) => (
              <tr key={i} className="hover:bg-gray-50">
                {headers.map((header, j) => (
                  <td key={j} className="px-4 py-2 text-sm text-gray-900">
                    {String(row[header] ?? '')}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

const DiscussionCard = memo(function DiscussionCard({ 
  discussion, 
  onLike, 
  onComment 
}: { 
  discussion: Discussion;
  onLike: (id: string, isLiked: boolean) => void;
  onComment: (id: string) => void;
}) {
  const [showComments, setShowComments] = useState(false);
  const [replyContent, setReplyContent] = useState('');
  const { toast } = useToast();

  const { data: comments = [] } = useQuery({
    queryKey: [`/api/community/posts/${discussion.id}/comments`, discussion.id],
    enabled: showComments,
  });

  const replyMutation = useMutation({
    mutationFn: async (content: string) => {
      return apiRequest('POST', `/api/community/posts/${discussion.id}/comments`, { content });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/community/posts/${discussion.id}/comments`] });
      queryClient.invalidateQueries({ queryKey: [`/api/problems/${discussion.problemId}/discussions`] });
      setReplyContent('');
      toast({ title: "Reply posted successfully!" });
    },
    onError: () => {
      toast({ title: "Failed to post reply", variant: "destructive" });
    },
  });

  const handleReply = () => {
    if (!replyContent.trim()) return;
    replyMutation.mutate(replyContent);
  };

  return (
    <Card className="mb-4" data-testid={`discussion-${discussion.id}`}>
      <CardContent className="p-6">
        <div className="flex items-start space-x-4">
          <Avatar className="w-10 h-10">
            <AvatarImage src={discussion.user.profileImageUrl} alt={discussion.user.username} />
            <AvatarFallback>
              {discussion.user.username?.charAt(0).toUpperCase() || 'U'}
            </AvatarFallback>
          </Avatar>

          <div className="flex-1">
            <div className="flex items-center space-x-2 mb-2">
              <span className="font-semibold text-foreground" data-testid={`text-username-${discussion.id}`}>
                {discussion.user.username}
              </span>
              <span className="text-sm text-muted-foreground">
                {new Date(discussion.createdAt).toLocaleDateString()}
              </span>
            </div>

            <p className="text-foreground mb-3 whitespace-pre-wrap" data-testid={`text-content-${discussion.id}`}>
              {discussion.content}
            </p>

            {discussion.codeSnippet && (
              <div className="bg-muted/50 rounded-lg p-3 mb-3">
                <pre className="text-sm font-mono whitespace-pre-wrap" data-testid={`code-snippet-${discussion.id}`}>
                  {discussion.codeSnippet}
                </pre>
              </div>
            )}

            <div className="flex items-center space-x-4">
              <Button
                variant="ghost"
                size="sm"
                onClick={() => onLike(discussion.id, !!discussion.isLiked)}
                className="text-muted-foreground hover:text-red-500"
                data-testid={`button-like-${discussion.id}`}
              >
                <Heart className={`w-4 h-4 mr-1 ${discussion.isLiked ? 'fill-current text-red-500' : ''}`} />
                {discussion.likes}
              </Button>

              <Button
                variant="ghost"
                size="sm"
                onClick={() => setShowComments(!showComments)}
                className="text-muted-foreground hover:text-blue-500"
                data-testid={`button-comments-${discussion.id}`}
              >
                <MessageSquare className="w-4 h-4 mr-1" />
                {discussion.comments}
              </Button>
            </div>

            {showComments && (
              <div className="mt-4 space-y-4">
                {/* Reply Input */}
                <div className="flex items-start space-x-3">
                  <Textarea
                    placeholder="Write a reply..."
                    value={replyContent}
                    onChange={(e) => setReplyContent(e.target.value)}
                    rows={2}
                    className="resize-none"
                    data-testid={`textarea-reply-${discussion.id}`}
                  />
                  <Button 
                    size="sm" 
                    onClick={handleReply}
                    disabled={!replyContent.trim() || replyMutation.isPending}
                    data-testid={`button-send-reply-${discussion.id}`}
                  >
                    <Send className="w-4 h-4" />
                  </Button>
                </div>

                {/* Comments */}
                <div className="space-y-3 pl-4 border-l-2 border-muted">
                  {comments.map((comment: Comment) => (
                    <NestedComment 
                      key={comment.id} 
                      comment={comment} 
                      discussionId={discussion.id}
                      onReply={() => {}} // Handled internally by NestedComment
                      depth={0}
                    />
                  ))}
                </div>
              </div>
            )}
          </div>
        </div>
      </CardContent>
    </Card>
  );
});

const CreateDiscussionDialog = memo(function CreateDiscussionDialog({ 
  problemId, 
  onClose 
}: { 
  problemId: string;
  onClose: () => void;
}) {
  const [content, setContent] = useState('');
  const [codeSnippet, setCodeSnippet] = useState('');
  const { toast } = useToast();

  const createMutation = useMutation({
    mutationFn: async (data: { content: string; codeSnippet?: string }) => {
      return apiRequest('POST', `/api/problems/${problemId}/discussions`, {
          content: data.content,
          codeSnippet: data.codeSnippet || undefined,
        });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/problems/${problemId}/discussions`] });
      setContent('');
      setCodeSnippet('');
      onClose();
      toast({ title: "Discussion posted successfully!" });
    },
    onError: () => {
      toast({ title: "Failed to create discussion", variant: "destructive" });
    },
  });

  const handleSubmit = () => {
    if (!content.trim()) return;
    createMutation.mutate({ content, codeSnippet });
  };

  return (
    <DialogContent className="max-w-2xl" data-testid="dialog-create-discussion">
      <DialogHeader>
        <DialogTitle>Start a New Discussion</DialogTitle>
      </DialogHeader>
