import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Badge } from '@/components/ui/badge';
import { Trash2, Plus, CheckCircle, XCircle, Info } from 'lucide-react';
import { useAdmin } from '@/contexts/AdminContext';

export function DataSourceTab() {
  const { state, actions } = useAdmin();
  const [solutionPath, setSolutionPath] = useState('');

  // Unified dataset management - supports both single and multiple datasets
  const datasets = state.datasets.length > 0 ? state.datasets : [{ bucket: '', key: '', table_name: '', description: '' }];

  const addDataset = () => {
    actions.setDatasets([
      ...state.datasets,
      { bucket: '', key: '', table_name: '', description: '' }
    ]);
  };

  const removeDataset = (index: number) => {
    if (state.datasets.length > 1) {
      actions.setDatasets(
        state.datasets.filter((_, i) => i !== index)
      );
    }
  };

  const updateDataset = (index: number, field: string, value: string) => {
    const updated = [...state.datasets];
    updated[index] = { ...updated[index], [field]: value };
    actions.setDatasets(updated);
  };

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader>
          <CardTitle>Data Source Management</CardTitle>
          <p className="text-sm text-muted-foreground">
            Validate S3 datasets and configure solution verification. 
            Supports single or multiple datasets. Apply validated data to Create Question tab.
          </p>
        </CardHeader>
      </Card>

      <div className="space-y-4">
        <Card>
          <CardHeader>
            <div className="flex justify-between items-center">
              <CardTitle>S3 Dataset Configuration</CardTitle>
              <Button
                onClick={addDataset}
                variant="outline"
                size="sm"
                data-testid="button-add-dataset"
              >
                <Plus className="w-4 h-4 mr-1" />
                Add Dataset
              </Button>
            </div>
            <p className="text-sm text-muted-foreground">
              Configure one or more S3 datasets. Each dataset will be loaded as a separate table.
            </p>
          </CardHeader>
          <CardContent className="space-y-4">
            {datasets.map((dataset, index) => (
              <div key={index} className="border rounded-lg p-4 space-y-3">
                <div className="flex justify-between items-center">
                  <h4 className="font-medium">
                    {datasets.length === 1 ? "Dataset" : `Dataset ${index + 1}`}
                  </h4>
                  {datasets.length > 1 && (
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => removeDataset(index)}
                      data-testid={`button-remove-dataset-${index}`}
                    >
                      <Trash2 className="w-4 h-4" />
                    </Button>
                  )}
                </div>

                <div className="grid grid-cols-2 gap-3">
                  <div>
                    <Label>S3 Bucket Name</Label>
                    <Input
                      value={dataset.bucket}
                      onChange={(e) => updateDataset(index, 'bucket', e.target.value)}
                      placeholder="my-datasets-bucket"
                      data-testid={`input-dataset-${index}-bucket`}
                    />
                    <p className="text-xs text-muted-foreground">
                      The S3 bucket containing your dataset
                    </p>
                  </div>
                  <div>
                    <Label>S3 Object Key</Label>
                    <Input
                      value={dataset.key}
                      onChange={(e) => updateDataset(index, 'key', e.target.value)}
                      placeholder="datasets/table1.parquet"
                      data-testid={`input-dataset-${index}-key`}
                    />
                    <p className="text-xs text-muted-foreground">
                      The path to your parquet file within the bucket
                    </p>
                  </div>
                  <div>
                    <Label>Table Name in DuckDB</Label>
                    <Input
                      value={dataset.table_name}
                      onChange={(e) => updateDataset(index, 'table_name', e.target.value)}
                      placeholder={`table_${index + 1}`}
                      data-testid={`input-dataset-${index}-table-name`}
                    />
                    <p className="text-xs text-muted-foreground">
                      What to call this table in the SQL environment
                    </p>
                  </div>
                  <div>
                    <Label>Description (Optional)</Label>
                    <Input
                      value={dataset.description}
                      onChange={(e) => updateDataset(index, 'description', e.target.value)}
                      placeholder="Dataset description"
                      data-testid={`input-dataset-${index}-description`}
                    />
                    <p className="text-xs text-muted-foreground">
                      Brief description of this dataset
                    </p>
                  </div>
                </div>

                <div className="p-3 bg-gray-50 dark:bg-gray-800 rounded-md">
                  <p className="text-sm text-gray-600 dark:text-gray-400">
                    <strong>S3 Data Source:</strong> s3://{dataset.bucket}/{dataset.key} → {dataset.table_name}
                  </p>
                </div>
              </div>
            ))}

            <div className="space-y-4 pt-4 border-t">
              <div className="flex gap-2">
                <Button
                  onClick={() => actions.validateDatasets(solutionPath)}
                  disabled={state.isValidatingDatasets || datasets.some(d => !d.bucket?.trim() || !d.key?.trim())}
                  data-testid="button-validate-datasets"
                >
                  {state.isValidatingDatasets ? 'Validating...' : 'Validate Datasets'}
                </Button>

                {state.datasetValidation?.success && (
                  <Button
                    onClick={() => actions.applyValidationToDraft('unified')}
                    variant="outline"
                    data-testid="button-apply-datasets"
                  >
                    Apply to Draft
                  </Button>
                )}
              </div>

              {/* Validation Results */}
              {state.datasetValidation && (
                <Alert className={state.datasetValidation.success ? 'border-green-200' : 'border-red-200'}>
                  {state.datasetValidation.success ? (
                    <CheckCircle className="h-4 w-4 text-green-600" />
                  ) : (
                    <XCircle className="h-4 w-4 text-red-600" />
                  )}
                  <AlertDescription>
                    {state.datasetValidation.success ? (
                      <div>
                        <p className="font-medium text-green-800">✓ Dataset validation successful!</p>
                        <p className="text-sm">
                          Found {state.datasetValidation.total_tables} table(s) with {state.datasetValidation.total_rows?.toLocaleString()} total rows
                        </p>
                        {state.datasetValidation.validated_datasets && (
                          <div className="mt-2 space-y-2">
                            {state.datasetValidation.validated_datasets.map((dataset, i) => (
                              <div key={i} className="text-sm border rounded p-2">
                                <p className="font-medium">{dataset.table_name}: {dataset.row_count?.toLocaleString()} rows</p>
                                <div className="flex flex-wrap gap-1 mt-1">
                                  {dataset.table_schema?.slice(0, 4).map((col, j) => (
                                    <Badge key={j} variant="outline" className="text-xs">
                                      {col.column} ({col.type})
                                    </Badge>
                                  ))}
                                  {(dataset.table_schema?.length || 0) > 4 && (
                                    <Badge variant="outline" className="text-xs">
                                      +{(dataset.table_schema?.length || 0) - 4} more
                                    </Badge>
                                  )}
                                </div>
                              </div>
                            ))}
                          </div>
                        )}
                      </div>
                    ) : (
                      <div>
                        <p className="font-medium text-red-800">✗ Dataset validation failed</p>
                        <p className="text-sm">{state.datasetValidation.error}</p>
                      </div>
                    )}
                  </AlertDescription>
                </Alert>
              )}
            </div>
          </CardContent>
        </Card>

        <div className="bg-yellow-50 dark:bg-yellow-900/20 border border-yellow-200 dark:border-yellow-800 rounded-md p-4">
          <div className="flex">
            <Info className="w-4 h-4 text-yellow-600 dark:text-yellow-400 mt-0.5 mr-2 flex-shrink-0" />
            <div className="text-sm">
              <p className="font-medium text-yellow-800 dark:text-yellow-200 mb-1">Solution Verification Method</p>
              <div className="space-y-2">
                <label className="flex items-center space-x-2">
                  <input
                    type="radio"
                    name="solution-source"
                    value="neon"
                    checked={state.solutionVerification?.source === 'neon' || !state.solutionVerification}
                    onChange={() => actions.setSolutionVerification({ source: 'neon', verified: false })}
                  />
                  <span className="text-yellow-700 dark:text-yellow-300">Manual entry (recommended) - Enter expected results manually</span>
                </label>
              </div>
            </div>
          </div>
        </div>

        {/* Note for Neon verification */}
        {(!state.solutionVerification || state.solutionVerification?.source === 'neon') && (
          <div className="p-3 bg-blue-50 dark:bg-blue-950 rounded-md border border-blue-200 dark:border-blue-800">
            <p className="text-sm text-blue-700 dark:text-blue-200">
              💡 Using Neon database verification - solution will be validated against manually entered expected results in the Create Question tab.
            </p>
          </div>
        )}
      </div>
    </div>
  );
}

client/src/components/admin/SolutionsTab.content:
import { useState, useEffect } from 'react';
import { useQuery, useMutation } from '@tanstack/react-query';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Save, FileText, Plus } from 'lucide-react';
import { useAdmin } from '@/contexts/AdminContext';
import { useToast } from '@/hooks/use-toast';
import { queryClient } from '@/lib/queryClient';

interface Solution {
  id: string;
  problem_id: string;
  title: string;
  content: string;
  sql_code: string;
  is_official: boolean;
  created_at: string;
  creator: {
    id: string;
    username: string;
  };
}

interface SolutionForm {
  title: string;
  content: string;
  sql_code: string;
}

export function SolutionsTab() {
  const { state } = useAdmin();
  const { toast } = useToast();
  const [selectedProblemId, setSelectedProblemId] = useState('');
  const [solutionForm, setSolutionForm] = useState<SolutionForm>({
    title: '',
    content: '',
    sql_code: ''
  });

  // React Query for problems
  const { data: problems = [], isLoading: problemsLoading } = useQuery({
    queryKey: ['/api/problems'],
    enabled: state.isAuthenticated
  });

  // React Query for existing solution (new endpoint)
  const { data: existingSolution, isLoading: solutionLoading, error: solutionError } = useQuery({
    queryKey: ['/api/admin/problems', selectedProblemId, 'solution'],
    enabled: !!selectedProblemId && state.isAuthenticated,
    queryFn: async () => {
      const response = await fetch(`/api/admin/problems/${selectedProblemId}/solution`, {
        headers: {
          'Authorization': `Bearer ${state.adminKey}`
        }
      });
      if (!response.ok) {
        if (response.status === 404) {
          return null; // No solution exists yet
        }
        throw new Error('Failed to fetch solution');
      }
      return response.json();
    }
  });

  // Create or update solution mutation
  const saveSolutionMutation = useMutation({
    mutationFn: async (solutionData: SolutionForm) => {
      const response = await fetch(`/api/admin/problems/${selectedProblemId}/solutions`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${state.adminKey}`
        },
        body: JSON.stringify(solutionData)
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.detail || 'Failed to save solution');
      }

      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: existingSolution ? "Solution updated successfully" : "Solution created successfully"
      });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/problems', selectedProblemId, 'solution'] });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive"
      });
    }
  });

  const handleSubmit = () => {
    if (!selectedProblemId) {
      toast({
        title: "Error",
        description: "Please select a problem first",
        variant: "destructive"
      });
      return;
    }

    if (!solutionForm.title.trim() || !solutionForm.sql_code.trim()) {
      toast({
        title: "Error",
        description: "Please fill in the title and SQL code",
        variant: "destructive"
      });
      return;
    }

    saveSolutionMutation.mutate(solutionForm);
  };

  // Load existing solution data when problem changes
  useEffect(() => {
    if (existingSolution) {
      setSolutionForm({
        title: existingSolution.title || '',
        content: existingSolution.content || '',
        sql_code: existingSolution.sql_code || ''
      });
    } else {
      setSolutionForm({
        title: '',
        content: '',
        sql_code: ''
      });
    }
  }, [existingSolution, selectedProblemId]);

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader>
          <CardTitle>Solution Management</CardTitle>
          <p className="text-sm text-muted-foreground">
            Create or edit solutions for problems. Each problem has one solution.
          </p>
        </CardHeader>
      </Card>

      {/* Problem Selection */}
      <Card>
        <CardHeader>
          <CardTitle>Select Problem</CardTitle>
        </CardHeader>
        <CardContent>
          <div>
            <Label htmlFor="problem-select">Choose a Problem</Label>
            <select
              id="problem-select"
              value={selectedProblemId}
              onChange={(e) => setSelectedProblemId(e.target.value)}
              className="w-full p-2 border rounded-md"
              data-testid="select-problem"
            >
              <option value="">Choose a problem...</option>
              {problems.map((problem: any) => (
                <option key={problem.id} value={problem.id}>
                  {problem.title} ({problem.difficulty})
                </option>
              ))}
            </select>
          </div>
        </CardContent>
      </Card>

      {/* Solution Form */}
      {selectedProblemId && (
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              {existingSolution ? (
                <>
                  <FileText className="w-5 h-5" />
                  Edit Solution
                </>
              ) : (
                <>
                  <Plus className="w-5 h-5" />
                  Create Solution
                </>
              )}
            </CardTitle>
            {solutionLoading && (
              <p className="text-sm text-muted-foreground">Loading solution...</p>
            )}
            {existingSolution && (
              <Alert>
                <AlertDescription>
                  Editing existing solution for this problem.
                </AlertDescription>
              </Alert>
            )}
          </CardHeader>
          <CardContent className="space-y-4">
            <div>
              <Label htmlFor="solution-title">Solution Title</Label>
              <Input
                id="solution-title"
                value={solutionForm.title}
                onChange={(e) => setSolutionForm(prev => ({ ...prev, title: e.target.value }))}
                placeholder="e.g., Optimized JOIN solution"
                data-testid="input-solution-title"
              />
            </div>

            <div>
              <Label htmlFor="solution-content">Solution Explanation</Label>
              <Textarea
                id="solution-content"
                value={solutionForm.content}
                onChange={(e) => setSolutionForm(prev => ({ ...prev, content: e.target.value }))}
                placeholder="Explain the approach and key insights..."
                rows={4}
                data-testid="textarea-solution-content"
              />
            </div>

            <div>
              <Label htmlFor="solution-sql">SQL Code</Label>
              <Textarea
                id="solution-sql"
                value={solutionForm.sql_code}
                onChange={(e) => setSolutionForm(prev => ({ ...prev, sql_code: e.target.value }))}
                placeholder="SELECT ..."
                rows={8}
                className="font-mono text-sm"
                data-testid="textarea-solution-sql"
              />
            </div>

            <div className="flex justify-end">
              <Button 
                onClick={handleSubmit}
                disabled={!solutionForm.title?.trim() || !solutionForm.sql_code?.trim() || saveSolutionMutation.isPending}
                data-testid="button-submit-solution"
              >
                <Save className="w-4 h-4 mr-2" />
                {saveSolutionMutation.isPending 
                  ? 'Saving...' 
                  : existingSolution 
                    ? 'Update Solution' 
                    : 'Create Solution'
                }
              </Button>
            </div>
          </CardContent>
        </Card>
      )}

      {!selectedProblemId && (
        <Alert>
          <AlertDescription>
            Please select a problem above to create or edit its solution.
          </AlertDescription>
        </Alert>
      )}
    </div>
  );
}

client/src/components/admin/EnhancedTabl.content:
import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Badge } from '@/components/ui/badge';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from '@/components/ui/table';
import { Edit2, Save, X, Eye, EyeOff, Database, Columns, Hash } from 'lucide-react';

interface TableColumn {
  name: string;
  type: string;
}

interface TableData {
  name: string;
  columns: TableColumn[];
  sample_data: Record<string, any>[];
}

interface EnhancedTablePreviewProps {
  tables: TableData[];
  onTableUpdate?: (tables: TableData[]) => void;
  readOnly?: boolean;
}

const COLUMN_TYPES = [
  'VARCHAR', 'TEXT', 'INTEGER', 'BIGINT', 'DECIMAL', 'FLOAT', 'DOUBLE',
  'BOOLEAN', 'DATE', 'TIMESTAMP', 'TIME', 'JSON', 'UUID'
];

export function EnhancedTablePreview({ tables, onTableUpdate, readOnly = false }: EnhancedTablePreviewProps) {
  const [editingTable, setEditingTable] = useState<number | null>(null);
  const [showSampleData, setShowSampleData] = useState<{ [key: number]: boolean }>({});
  const [editedTables, setEditedTables] = useState<TableData[]>([]);

  // Deep clone helper function
  const deepCloneTables = (tablesToClone: TableData[]): TableData[] => {
    return tablesToClone.map(table => ({
      name: table.name,
      columns: table.columns.map(col => ({
        name: col.name,
        type: col.type
      })),
      sample_data: table.sample_data.map(row => ({ ...row }))
    }));
  };

  const handleEditTable = (index: number) => {
    setEditingTable(index);
    // Create a deep clone to prevent mutations to the original data
    setEditedTables(deepCloneTables(tables));
  };

  const handleSaveTable = (index: number) => {
    if (onTableUpdate) {
      // Pass the deep-cloned edited tables to the parent
      onTableUpdate(deepCloneTables(editedTables));
    }
    setEditingTable(null);
    setEditedTables([]);
  };

  const handleCancelEdit = () => {
    setEditingTable(null);
    // Clear the edited tables to prevent any lingering references
    setEditedTables([]);
  };

  const handleColumnNameChange = (tableIndex: number, columnIndex: number, newName: string) => {
    const updated = deepCloneTables(editedTables);
    updated[tableIndex].columns[columnIndex].name = newName;
    setEditedTables(updated);
  };

  const handleColumnTypeChange = (tableIndex: number, columnIndex: number, newType: string) => {
    const updated = deepCloneTables(editedTables);
    updated[tableIndex].columns[columnIndex].type = newType;
    setEditedTables(updated);
  };

  const handleTableNameChange = (tableIndex: number, newName: string) => {
    const updated = deepCloneTables(editedTables);
    updated[tableIndex].name = newName;
    setEditedTables(updated);
  };

  const toggleSampleData = (tableIndex: number) => {
    setShowSampleData(prev => ({
      ...prev,
      [tableIndex]: !prev[tableIndex]
    }));
  };

  const renderSampleData = (table: TableData) => {
    if (!table.sample_data || table.sample_data.length === 0) return null;

    const headers = Object.keys(table.sample_data[0]);
    const displayRows = table.sample_data.slice(0, 5); // Show first 5 rows

    return (
      <div className="mt-4 border rounded-lg overflow-hidden">
        <div className="bg-muted/30 px-3 py-2 border-b">
          <h5 className="text-sm font-medium text-muted-foreground">Sample Data</h5>
        </div>
        <div className="overflow-x-auto">
          <Table>
            <TableHeader>
              <TableRow className="bg-muted/20">
                {headers.map((header) => (
                  <TableHead key={header} className="font-medium text-xs">
                    {header}
                  </TableHead>
                ))}
              </TableRow>
            </TableHeader>
            <TableBody>
              {displayRows.map((row, index) => (
                <TableRow key={index} className="text-xs">
                  {headers.map((header) => (
                    <TableCell key={header} className="py-1 px-2 max-w-32 truncate">
                      {String(row[header] ?? '')}
                    </TableCell>
                  ))}
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </div>
        {table.sample_data.length > 5 && (
          <div className="px-3 py-2 text-xs text-muted-foreground bg-muted/10 border-t">
            ... and {table.sample_data.length - 5} more rows
          </div>
        )}
      </div>
    );
  };

  if (tables.length === 0) {
    return (
      <Card>
        <CardContent className="p-6 text-center text-muted-foreground">
          <Database className="mx-auto h-12 w-12 mb-3 opacity-50" />
          <p>No tables loaded yet</p>
          <p className="text-sm">Load tables from a datasource to see the preview</p>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-4">
      <div className="flex items-center justify-between">
        <h3 className="text-lg font-semibold flex items-center gap-2">
          <Database className="h-5 w-5" />
          Tables Preview ({tables.length})
        </h3>
        {!readOnly && (
          <Badge variant="outline" className="text-xs">
            Click table names or columns to edit
          </Badge>
        )}
      </div>

      {tables.map((table, tableIndex) => {
        const isEditing = editingTable === tableIndex;
        const currentTable = isEditing && editedTables.length > 0 ? editedTables[tableIndex] : table;

        return (
          <Card key={tableIndex} className="overflow-hidden">
            <CardHeader className="pb-3">
              <div className="flex items-center justify-between">
                <div className="flex items-center gap-3">
                  {isEditing ? (
                    <Input
                      value={currentTable.name}
                      onChange={(e) => handleTableNameChange(tableIndex, e.target.value)}
                      className="text-lg font-semibold h-auto py-1 px-2 w-48"
                      data-testid={`input-table-name-${tableIndex}`}
                    />
                  ) : (
                    <CardTitle 
                      className={`flex items-center gap-2 ${!readOnly ? 'cursor-pointer hover:text-primary transition-colors' : ''}`}
                      onClick={() => !readOnly && handleEditTable(tableIndex)}
                      data-testid={`text-table-name-${tableIndex}`}
                    >
                      <Columns className="h-4 w-4 text-primary" />
                      {table.name}
                    </CardTitle>
                  )}

                  <div className="flex items-center gap-2 text-sm text-muted-foreground">
                    <Badge variant="secondary" className="text-xs bg-blue-50 text-blue-700 dark:bg-blue-900 dark:text-blue-300">
                      <Hash className="h-3 w-3 mr-1" />
                      {table.columns.length} column{table.columns.length !== 1 ? 's' : ''}
                    </Badge>
                    <Badge variant="secondary" className="text-xs bg-green-50 text-green-700 dark:bg-green-900 dark:text-green-300">
                      <Database className="h-3 w-3 mr-1" />
                      {table.sample_data.length} row{table.sample_data.length !== 1 ? 's' : ''}
                    </Badge>
                  </div>
                </div>

                <div className="flex items-center gap-2">
                  {table.sample_data.length > 0 && (
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => toggleSampleData(tableIndex)}
                      data-testid={`button-toggle-sample-${tableIndex}`}
                    >
                      {showSampleData[tableIndex] ? (
                        <>
                          <EyeOff className="h-4 w-4 mr-1" />
                          Hide Data
                        </>
                      ) : (
                        <>
                          <Eye className="h-4 w-4 mr-1" />
                          Show Data
                        </>
                      )}
                    </Button>
                  )}

                  {!readOnly && (
                    <>
                      {isEditing ? (
                        <div className="flex gap-1">
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={() => handleSaveTable(tableIndex)}
                            data-testid={`button-save-table-${tableIndex}`}
                          >
                            <Save className="h-4 w-4" />
                          </Button>
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={handleCancelEdit}
                            data-testid={`button-cancel-edit-${tableIndex}`}
                          >
                            <X className="h-4 w-4" />
                          </Button>
                        </div>
                      ) : (
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => handleEditTable(tableIndex)}
                          data-testid={`button-edit-table-${tableIndex}`}
                        >
                          <Edit2 className="h-4 w-4 mr-1" />
                          Edit
                        </Button>
                      )}
                    </>
                  )}
                </div>
              </div>
            </CardHeader>

            <CardContent className="pt-0">
              {/* Schema Display */}
              <div className="border rounded-lg overflow-hidden">
                <div className="bg-muted/30 px-3 py-2 border-b">
                  <h5 className="text-sm font-medium text-muted-foreground">Table Schema</h5>
                </div>
                <Table>
                  <TableHeader>
                    <TableRow className="bg-muted/20">
                      <TableHead className="font-medium">Column Name</TableHead>
                      <TableHead className="font-medium">Data Type</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {currentTable.columns.map((column, columnIndex) => (
                      <TableRow key={columnIndex}>
                        <TableCell className="py-2">
                          {isEditing ? (
                            <Input
                              value={column.name}
                              onChange={(e) => handleColumnNameChange(tableIndex, columnIndex, e.target.value)}
                              className="font-mono text-sm h-8"
                              data-testid={`input-column-name-${tableIndex}-${columnIndex}`}
                            />
                          ) : (
                            <span 
                              className={`font-mono text-sm ${!readOnly ? 'cursor-pointer hover:text-primary' : ''}`}
                              onClick={() => !readOnly && handleEditTable(tableIndex)}
                              data-testid={`text-column-name-${tableIndex}-${columnIndex}`}
                            >
                              {column.name}
                            </span>
                          )}
                        </TableCell>
                        <TableCell className="py-2">
                          {isEditing ? (
                            <select
                              value={column.type}
                              onChange={(e) => handleColumnTypeChange(tableIndex, columnIndex, e.target.value)}
                              className="h-8 w-full border rounded px-2 text-sm"
                              data-testid={`select-column-type-${tableIndex}-${columnIndex}`}
                            >
                              {COLUMN_TYPES.map((type) => (
                                <option key={type} value={type}>
                                  {type}
                                </option>
                              ))}
                            </select>
                          ) : (
                            <Badge 
                              variant="outline" 
                              className={`text-xs ${!readOnly ? 'cursor-pointer hover:bg-primary/10' : ''}`}
                              onClick={() => !readOnly && handleEditTable(tableIndex)}
                              data-testid={`text-column-type-${tableIndex}-${columnIndex}`}
                            >
                              {column.type}
                            </Badge>
                          )}
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </div>

              {/* Sample Data */}
              {showSampleData[tableIndex] && renderSampleData(table)}
            </CardContent>
          </Card>
        );
      })}
    </div>
  );
}
